import React, { createContext, useContext } from 'react';

export const FunctionContext = createContext();

const sumItems = (instances) => {
  var checkedInstances = instances.filter(
    (instance) => instance.check === true
  );
  let itemCount = checkedInstances.reduce(
    (total, checkedInstance) => total + checkedInstance.quantity,
    0
  );
  let total = instances
    .reduce(
      (total, instance) => total + instance.item.price * instance.quantity,
      0
    )
    .toFixed(2);
  console.log('itemCount in sunItems: ', itemCount);
  return { itemCount, total };
};
const ChgChk = (parmscheck, parmsid, parmsuser, fnc, fnt) => {
  return fetch('/api/user_chkchg_post', {
    method: 'post',
    body: JSON.stringify({
      check: parmscheck,
      instanceid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      var { itemCount, total } = sumItems(result.data);
      fnc(itemCount);
      fnt(total);
    })
    .catch(new Error());
};
const DeleteIns = (parmsid, parmsuser, fnins, fnlen, fnc, fnt) => {
  return fetch('/api/user_deleteins_post', {
    method: 'post',
    body: JSON.stringify({
      instanceid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      fnins(result.data);
      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);

      var { itemCount, total } = sumItems(result.data);
      fnc(itemCount);
      fnt(total);

      console.log('inslen in GetAllCartst: ', inslen);
    })
    .catch(new Error());
};

const ChangeAddCart = (
  parmscount,
  parmsid,
  parmsuser,
  fnins,
  fnlen,
  fnc,
  fnt
) => {
  return fetch('/api/user_changeAddcart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      instanceid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      fnins(result.data);

      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);
      console.log('inslen in GetAllCartst: ', inslen);

      var { itemCount, total } = sumItems(result.data);
      fnc(itemCount);
      fnt(total);
    })
    .catch(new Error());
};

const ChangeToCart = (
  parmscount,
  parmsid,
  parmsuser,
  fnins,
  fnlen,
  fnc,
  fnt
) => {
  return fetch('/api/user_changetocart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      instanceid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      fnins(result.data);

      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);
      console.log('inslen in GetAllCartst: ', inslen);
      var { itemCount, total } = sumItems(result.data);
      fnc(itemCount);
      fnt(total);
    })
    .catch(new Error());
};
const checkdb = () => {
  return fetch('/api/del_carts_all')
    .then(console.log('delete success in function'))
    .catch((err) => new Error(err));
};
const GetAllCarts = (parmsuser, fnins, fnlen, fnc, fnt) => {
  if (!parmsuser) return Promise.reject('no user');
  return fetch('/api/user_getallcart_post', {
    method: 'post',
    body: JSON.stringify({
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      fnins(result.data);

      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;

      fnlen(inslen);

      var { itemCount, total } = sumItems(result.data);
      fnc(itemCount);
      fnt(total);
      console.log('count: ', itemCount);
      console.log('total: ', total);
    })
    .catch(new Error());
};

const AddToCart = (parmscount, parmsid, parmsuser, fnins, fnlen, fnc, fnt) => {
  return fetch('/api/user_addtocart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      itemid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      fnins(result.data);

      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);

      var { itemCount, total } = sumItems(result.data);
      fnc(itemCount);
      fnt(total);

      console.log('inslen in GetAllCartst: ', inslen);
    })
    .catch(new Error());
};

const FunctionContextProvider = ({ children }) => {
  return (
    <FunctionContext.Provider
      value={{
        GetAllCarts,
        AddToCart,
        checkdb,
        ChangeToCart,
        ChangeAddCart,
        DeleteIns,
        ChgChk,
      }}
    >
      {children}
    </FunctionContext.Provider>
  );
};

export const usefunctions = () => useContext(FunctionContext);
export default FunctionContextProvider;
