import styles from './about/about.module.css';
import { useRef, useState } from 'react';
export default function About() {
    const divref = useRef(null);
    const img1ref = useRef(null);
    const img2ref = useRef(null);
    const img3ref = useRef(null);
    const img4ref = useRef(null);
    const rsltref = useRef(null);
    var images = [
        '/uploads/0fec1a5414eecd7c7f3213d41204d491',
        '/uploads/1ea5055277b39956f5273440f490b0e6',
        'uploads/6d24b02bfe7e19cccb870933a2126816',
        '/uploads/7da9c650ff9470b29c3744ec25f40032',
    ];
    const [image, setImage] = useState(
        '/uploads/0fec1a5414eecd7c7f3213d41204d491'
    );
    const [count, setCount] = useState(0);
    const handleHover = () => {
        setHover(true);
    };

    const handlestatic = () => {
        setHover(false);

        //src = imgref.current.attributes[0]
    };
    const handleImage = () => {
        console.log();
    };

    const click1 = () => {
        setImage(img1ref.current.attributes[0].nodeValue);
    };

    const click2 = () => {
        setImage(img2ref.current.attributes[0].nodeValue);
    };

    const click3 = () => {
        setImage(img3ref.current.attributes[0].nodeValue);
    };

    const click4 = () => {
        setImage(img4ref.current.attributes[0].nodeValue);
    };

    const slideshow = () => {
        setCount((count + 1) % images.length);
        setImage(images[count]);
    };
    return (
        <div>
            <div className="row">
                <div className="column">
                    <img
                        ref={img1ref}
                        src="/uploads/0fec1a5414eecd7c7f3213d41204d491"
                        alt="Nature"
                        onMouseEnter={click1}
                        height={200}
                        width={200}
                    />
                </div>
                <div className="column">
                    <img
                        ref={img2ref}
                        src="/uploads/1ea5055277b39956f5273440f490b0e6"
                        alt="Snow"
                        onMouseEnter={click2}
                        height={200}
                        width={200}
                    />
                </div>
                <div className="column">
                    <img
                        ref={img3ref}
                        src="uploads/6d24b02bfe7e19cccb870933a2126816"
                        alt="Mountains"
                        onMouseEnter={click3}
                        height={200}
                        width={200}
                    />
                </div>
                <div className="column">
                    <img
                        ref={img4ref}
                        src="/uploads/7da9c650ff9470b29c3744ec25f40032"
                        alt="Lights"
                        onMouseEnter={click4}
                        height={200}
                        width={200}
                    />
                </div>
            </div>

            <div className="container">
                <span
                    // onClick="this.parentElement.style.display='none'"
                    className="closebtn"
                >
                    &times;
                </span>
                <button onClick={slideshow}>+</button>
                <img id="expandedImg" src={image} height={400} width={400} />
                <button onClick="">-</button>
                <div id="imgtext"></div>
            </div>
        </div>
    );
}
