const Storage = (cartItems) => {
  localStorage.setItem(
    'cart',
    JSON.stringify(cartItems.length > 0 ? cartItems : [])
  );
};

export const sumItems = (instances) => {
  Storage(cartItems);
  let itemCount = instances.reduce(
    (total, instance) => total + instance.quantity,
    0
  );
  let total = instances
    .reduce((total, instance) => total + product.price * product.quantity, 0)
    .toFixed(2);
  return { itemCount, total };
};
