import { useEffect } from 'react';
import Link from 'next/link';
import Layout from '../components/layout';
import CartInstances from '../components/CartInstances';
import { useUserDispatch, useUser } from './contexts/userContext';
import { useIns, useInsDispatch } from './contexts/CartContext';
import { usefunctions } from './contexts/functionContext';
import { useInitial, useInitialDispatch } from './contexts/initialContext';
import { formatNumber } from '../utils/utils';

export default function ToCheckOut() {
  var temp;
  const user = useUser();
  const setUser = useUserDispatch();

  const { instances, itemCount, total } = useIns();
  const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch();
  const { GetAllCarts, checkdb, toCheckOut } = usefunctions();

  const setInitial = useInitialDispatch();
  const inital = useInitial();

  useEffect(() => {
    if (!inital) {
      console.log('I am in intial');
      //set user
      if (sessionStorage.getItem('key')) {
        temp = sessionStorage.getItem('key');
      } else {
        temp = Math.random()
          .toString(36)
          .replace(/[^a-z]+/g, '')
          .substr(0, 12);
        sessionStorage.setItem('key', temp);
      }

      setUser(temp);

      user &&
        GetAllCarts(user, setInstance, setInsLen, setItemCount, setTotal).then(
          () => {
            setInitial(true);
          }
        );
    }
  }, [user, itemCount]);

  const handleToChk = () => {
    toCheckOut(user, setItemCount, setTotal);
  };

  return (
    <Layout>
      <button onClick={checkdb}>check database</button>
      {/* Create a card for each item */}
      <div className='container'>
        {console.log('instances : ', instances)}
        {instances &&
          instances.filter((instance)=>instance.check).map((iteminstance) =>     
           <div key={iteminstance._id} className='row'>
        <div className='col d-flex '>
          <div>
            <input
              type='checkbox'
              //value='true'
              ref={checkEL}
              defaultChecked={iteminstance.check}
              onChange={() => handleCheck(iteminstance)}
            />
          </div>
          <div>
            <Link href='/store/[id]' as={`store/${iteminstance.item._id}`}>
              <a className='btn btn-link btn-sm mr-2'>
                <Image
                  src={`/${iteminstance.item.image}`}
                  height={70}
                  width={70}
                />
              </a>
            </Link>
          </div>
          <div className='align-self-center mr-2'>
            <Link href='/store/[id]' as={`store/${iteminstance.item._id}`}>
              <a className='btn btn-link btn-sm  '>{iteminstance.item.title}</a>
            </Link>
          </div>

          <div className=' align-self-center mr-2'>
            unit price: {formatNumber(iteminstance.item.price)}
          </div>

          <div className={styles.inputwraper1}>
            <button
              onClick={() => handleDecrease(iteminstance)}
              className={styles.minus1}
              disabled={decdis}
            >
              - minus
            </button>
            <input
              type='text'
              ref={inputEL}
              onInput={handleCount}
              onBlur={handlefocus}
              //onChange={handleCount}
              className={styles.input1}
              defaultValue={iteminstance.quantity}
            />
            <button
              onClick={() => handleIncrease(iteminstance)}
              className={styles.add1}
              disabled={incdis}
            >
              + add
            </button>
          </div>
          <div className='align-self-center mr-3'>
            price: {formatNumber(price)}
          </div>
          <div className='align-self-center'>
            <button
              onClick={() => handleDelIns(iteminstance)}
              className='btn btn-outline-primary btn-sm disable '
              disabled={dltdis}
            >
              Delete Item
            </button>
          </div>
        </div>
      </div>)}
        {(!instances || instances.length == 0) && <div>No items</div>}
        <div className='row'>
          <div className='mr-3'>Total count: {itemCount}</div>
          <div className='mr-3'>Total price: {formatNumber(total)}</div>

          <Link href='/cart/tocheckout'>
                <a className='btn btn-success'>check out </a>
              </Link>
        </div>
      </div>
      <footer></footer>
    </Layout>
  );
}
