import Head from 'next/head';
import Link from 'next/link';
import { useIns, useInsDispatch } from '../pages/contexts/CartContext';

import { useUser, useUserDispatch } from '../pages/contexts/userContext';

import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
export default function Layout({
    children,
    title = 'This is the default title',
}) {
    const { inslen } = useIns();
    const { user, tmpuser } = useUser();
    const { setInstances } = useInsDispatch();
    const { setUser } = useUserDispatch();
    const handleLogout = () => {
        setUser('');
        setInstances('');
    };
    return (
        <div>
            <Head>
                <title>{title}</title>
                <meta charSet="utf-8" />
                <meta
                    name="viewport"
                    content="initial-scale=1.0, width=device-width"
                />
            </Head>

            <Navbar bg="light" expand="lg" sticky="top">
                <Navbar.Brand>
                    <Link href="/">
                        <a>Home</a>
                    </Link>
                </Navbar.Brand>

                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Link href="/about">
                            <a className="nav-link">About</a>
                        </Link>
                        <Link href="/contact">
                            <a className="nav-link">Contact</a>
                        </Link>
                    </Nav>
                    <Nav>
                        {!user && (
                            <NavDropdown
                                title={`Hello ${tmpuser}`}
                                id="collasible-nav-dropdown"
                            >
                                <Link href="/login">
                                    <a className="nav-link">Login</a>
                                </Link>

                                <Link href="/register">
                                    <a className="nav-link">New? register</a>
                                </Link>

                                <NavDropdown.Divider />

                                <Link href="/order">
                                    <a className="nav-link">Order</a>
                                </Link>
                            </NavDropdown>
                        )}
                        {user && (
                            <NavDropdown
                                title={`Hello ${user}`}
                                id="collasible-nav-dropdown"
                            >
                                <Container>
                                    <Link href="/order">
                                        <a className="nav-link">Order</a>
                                    </Link>
                                    <NavDropdown.Divider />
                                    <a
                                        className="nav-link"
                                        style={{ cursor: 'pointer' }}
                                        onClick={handleLogout}
                                    >
                                        Logout
                                    </a>
                                </Container>
                            </NavDropdown>
                        )}

                        <Link href="/cart">
                            <a className="nav-link">Cart ({inslen})</a>
                        </Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            {children}
        </div>
    );
}
