import { useState, useRef } from 'react';
import Link from 'next/link';
import { useInsDispatch } from '../pages/contexts/CartContext';
import { useUser } from '../pages/contexts/userContext';
import { usefunctions } from '../pages/contexts/functionContext';

export default function CartInstances({ iteminstance }) {
  const inputEL = useRef(null);
  const [count, setCount] = useState(iteminstance.quantity);
  var a;
  var oldValue;
  const input = document.getElementById('id1');

  const handleCount = (e) => {
    console.log('inputEL: ', inputEL);
    if (inputEL.current.value.length == 0) {
    }
    /*if (!inputEL.current.value.match('[1-9][0-9]*')) {
      if (inputEL.current.length == 0) {
        inputEL.current.value = '222';
      } else {
        inputEL.current.value = '333';
      }
    }*/
    // inputEL.current.focus();
    /*
    input.addEventListener('keyup', function (event) {
      // Each time the user types something, we check if the
      // form fields are valid.

      if (!input.validity.valid) {
        // In case there is an error message visible, if the field
        // is valid, we remove the error message.
        input.textContent = oldValue; // Reset the content of the message
        console.log('input.textContent: ', input.textContent);
        setCount(oldValue);
      } else {
        if (!input.value.length == 0) {
          setCount(input.value);
          oldValue = input.value;
          console.log('input.value: ', input.value);
        } else {
          setCount(oldValue);
        }
      }
    });*/
  };
  /* if (e.target.value.match('[1-9][0-9]*')) {
      setCount(e.target.value);
    } else {
      setCount(count);
    }
  };*/
  const user = useUser();
  const { AddToCart } = usefunctions();
  const { setInstance, setInsLen } = useInsDispatch();

  const handleIncrease = (e) => {
    //setCount(count + 1);
    //AddToCart(1, iteminstance.item._id, user, setInstance, setInsLen);
    /*   dispatch({
      type: 'INCREASE_BY',
      payload: 1,
    });*/
  };
  const handleDecrease = (e) => {
    // setCount(count - 1);
    // AddToCart(-1, iteminstance.item._id, user, setInstance, setInsLen);
    /*dispatch({
      type: 'DECREASE',
      payload: iteminstance,
      user: user,
    });*/
  };

  return (
    <>
      <input
        id='id1'
        type='number'
        ref={inputEL}
        onChange={handleCount}
        className='form-control input-lg'
        pattern='[1-9][0-9]*'
        style={{ width: '100px' }}
        defaultValue={iteminstance.quantity}
      />
      <div key={iteminstance._id}>
        <div className='card card-body'>
          <img
            src={iteminstance.item.image}
            style={{
              display: 'block',
              margin: '0 auto 10px',
              height: '200px',
              width: '200px',
            }}
          />
          <p className='item-name'>{iteminstance.item.title}</p>

          <h3 className='text-left'>price: {iteminstance.item.price}</h3>

          <div className='text-right'>
            <Link href='/[id]/detail' as={`/${iteminstance.item._id}/detail`}>
              <a className='btn btn-link btn-sm mr-2'>detail</a>
            </Link>

            <button
              onClick={() => handleIncrease(iteminstance)}
              className='btn btn-outline-primary btn-sm'
              //  disabled={iteminstance.quantity >= 999}
            >
              + add
            </button>

            <button
              onClick={() => handleDecrease(iteminstance)}
              className='btn btn-outline-primary btn-sm'
              //  disabled={count <= 1}
            >
              - minus
            </button>

            {/*isInCart(product) && (
                <button
                  onClick={() => increase(product)}
                  className='btn btn-outline-primary btn-sm'
                >
                  Add more
                </button>
              )*/}

            {/*!isInCart(product) && (
                <button
                  onClick={() => addProduct(product)}
                  className='btn btn-primary btn-sm'
                >
                  Add to cart
                </button>
              )*/}
          </div>
        </div>
      </div>
    </>
  );
}
