import ItemInstance from '../../models/iteminstance';
import dbConnect from '../../utils/dbConnect';

import formidable from 'formidable';
import fs from 'fs';

export const config = {
    api: {
        externalResolver: true,
        bodyParser: false,
    },
};

export default async function handler(req, res) {
    const form = formidable({ multiples: true });
    var filearray = [];
    var singlefile, text;
    form.parse(req, (err, fields, files) => {
        if (err) {
            res.json(err);
            return;
        }

        console.log('files: ', files);
        console.log('fields:', fields);
        text = fields.textarea;
        // fields.textarea -> send to comment
        var oldPath, newPath;
        if (files.commentattach.length) {
            // var filestodb = [];
            for (let i = 0; i < files.commentattach.length; i++) {
                oldPath = files.commentattach[i].path;
                newPath = 'uploadsnew/' + files.commentattach[i].name;

                fs.renameSync(oldPath, newPath);
                console.log('Successfully renamed - AKA moved!');
                filearray.push(newPath); //file to db;
                /*   fs.rename(oldPath, newPath, function (err) {
                    if (err) throw err;
                    console.log('Successfully renamed - AKA moved!');
                    filearray.push(newPath); //file to db;
                });*/
            }
            console.log('filearray : ', filearray);
        } else {
            oldPath = files.commentattach.path;
            newPath = 'uploadsnew/' + files.commentattach.name;
            fs.renameSync(oldPath, newPath);
            console.log('Successfully renamed - AKA moved!');
            filearray.push(newPath); //file to db
            console.log('filearray: ', filearray);
            /* fs.rename(oldPath, newPath, function (err) {
                if (err) throw err;
                console.log('Successfully renamed - AKA moved!');
                filestodb = newPath; //file to db
                console.log('filestodb single: ', filestodb);
            });*/
        }
    });

    await dbConnect();
    ItemInstance.findByIdAndUpdate(req.body.id, {
        commenttext: text,
        commentimage: filearray,
    })
        .then((items_comment) => {
            console.log('I am here after update comment: ', items_comment);
            res.status(201).json({ data: items_comment });
            res.end();
        })
        .catch((err) => {
            console.log('I am in err: ', err);
            res.status(500).json({ error: err });
            res.end();
        });
}
