import Layout from '../../../components/layout';
import { useRouter } from 'next/router';
import { useRef, useState } from 'react';
import { useIns, useInsDispatch } from '../../contexts/CartContext';
import { usefunctions } from '../../contexts/functionContext';
import { useUser } from '../../contexts/userContext';

export default function Comment() {
    const [error, setError] = useState([]);
    const user = useUser();
    const textEL = useRef(null);
    const imgEL = useRef(null);
    const formEL = useRef(null);
    const { PostComment, GetAllOrder } = usefunctions();

    const { orders } = useIns();
    const { setOrders, setCmtmsg } = useInsDispatch();

    const router = useRouter();
    const ins = JSON.parse(router.query.ins);

    var images = ins.commentimage.map((url) => ({
        original: `/${url}`,
        thumbnail: `/${url}`,
    }));

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData(formEL.current);
        PostComment(formData, orders, setOrders, setError).then((res) => {
            if (res.status === 201) {
                // GetAllOrder(user, setOrders); //??
                setCmtmsg(ins._id);
                setTimeout(() => {
                    setCmtmsg('');
                }, 3000);
                //router.push(`/order/#${ins._id}`);
                router.back();
            }
        });
    };

    const print = (e) => {
        console.log('imgEL: ', imgEL);
        console.log('result', e.target);
    };
    const handleCancel = (e) => {
        e.preventDefault();
        router.back();
    };

    return (
        <Layout>
            <div className="container mt-3 ">
                <div>
                    <img src={`/${ins.item.image}`} height={70} width={70} />
                </div>
                <div>{ins.item.title}</div>

                <form
                    ref={formEL}
                    onSubmit={handleSubmit}
                    method="post"
                    encType="multipart/form-data"
                >
                    <label>Comment:</label>
                    {error && <p style={{ color: 'red' }}>{error}</p>}
                    <textarea
                        name="textarea"
                        ref={textEL}
                        className="form-control"
                        rows="5"
                        defaultValue={ins.commenttext}
                    ></textarea>

                    <input name="insid" value={ins._id} hidden readOnly />

                    <div className="custom-file">
                        <label className="custom-file-label">
                            <input
                                onChange={print}
                                name="commentattach"
                                ref={imgEL}
                                type="file"
                                className="file-control-file"
                                //   onChange={handleFile}
                                multiple="multiple"
                            />
                            Choose file
                        </label>
                    </div>
                    <button className="btn btn-success mr-3">submit</button>
                    <button
                        className="btn btn-success mr-3"
                        onClick={handleCancel}
                    >
                        cancel
                    </button>
                </form>
            </div>
        </Layout>
    );
}
