import { useEffect, useRef, useState } from 'react';

import Link from 'next/link';
import Layout from '../../components/layout';
import { useRouter } from 'next/router';
import dbConnect from '../../utils/dbConnect';
import Item from '../../models/item';
import Iteminstance from '../../models/iteminstance';
import Image from 'next/image';
import styles from '../../styles/store.module.css';
import { useIns, useInsDispatch } from '../../pages/contexts/CartContext';
import { useUser, useUserDispatch } from '../../pages/contexts/userContext';
import { usefunctions } from '../../pages/contexts/functionContext';
import { useInitial, useInitialDispatch } from '.././contexts/initialContext';
//import { TransitionGroup } from 'react-transition-group';
import { formatNumber } from '../../utils/utils';

export default function Post({ item, commentsins }) {
    const nextrouter = useRouter();
    var temp;
    const { id } = nextrouter.query;
    const inputEL = useRef(null);

    const [incdis, setIncdis] = useState(false);
    const [decdis, setDecdis] = useState(false);

    const [stylestab1, setStylestab1] = useState(styles.tabactive);
    const [stylestab2, setStylestab2] = useState(styles.tab);
    const [stylestab3, setStylestab3] = useState(styles.tab);
    const [stylestab4, setStylestab4] = useState(styles.tab);

    const user = useUser();
    const setUser = useUserDispatch();

    const { GetAllCarts, AddToCart } = usefunctions();
    const { instances } = useIns();
    const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch();

    const setInitial = useInitialDispatch();
    const inital = useInitial();

    const handleCount = (e) => {
        var v = e.target.value.replace(/\D+/g, '').replace(/^0+/, '');
        inputEL.current.value = v;
        setIncdis(parseInt(inputEL.current.value) >= 999);
        setDecdis(parseInt(inputEL.current.value) <= 1);
    };

    const handleCart = () => {
        setIncdis(true);
        setDecdis(true);

        //hanle DB -> plus value
        AddToCart(
            inputEL.current.value,
            item._id,
            user,
            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        ).then(() => {
            if (inputEL.current) {
                setIncdis(parseInt(inputEL.current.value) >= 999);
                setDecdis(parseInt(inputEL.current.value) <= 1);
            }
        });
    };
    const handleIncrease = (e) => {
        inputEL.current.value = parseInt(inputEL.current.value) + 1;
        if (inputEL.current) {
            setIncdis(parseInt(inputEL.current.value) >= 999);
            setDecdis(parseInt(inputEL.current.value) <= 1);
        }
    };

    const handleDecrease = (e) => {
        inputEL.current.value = parseInt(inputEL.current.value) - 1;
        if (inputEL.current) {
            setIncdis(parseInt(inputEL.current.value) >= 999);
            setDecdis(parseInt(inputEL.current.value) <= 1);
        }
    };

    const handlefocus = () => {
        !inputEL.current.value && (inputEL.current.value = 1);
        if (inputEL.current) {
            setIncdis(parseInt(inputEL.current.value) >= 999);
            setDecdis(parseInt(inputEL.current.value) <= 1);
        }
    };

    useEffect(() => {
        if (!inital) {
            //set user
            if (sessionStorage.getItem('key')) {
                temp = sessionStorage.getItem('key');
            } else {
                temp = Math.random()
                    .toString(36)
                    .replace(/[^a-z]+/g, '')
                    .substr(0, 12);
                sessionStorage.setItem('key', temp);
            }
            setUser(temp);

            user &&
                GetAllCarts(
                    user,
                    setInstance,
                    setInsLen,
                    setItemCount,
                    setTotal
                )
                    .then(() => {
                        setInitial(true);
                    })
                    .catch(new Error());
        }
        console.log('I am in effect2');
    }, [user]);

    useEffect(() => {
        setIncdis(parseInt(item.quantity) <= 1);
        setDecdis(parseInt(item.quantity) <= 1);
        console.log('I am in effect');
    }, []);

    const click1 = () => {};
    const click2 = () => {};
    const click3 = () => {};
    const click4 = () => {};
    const click5 = () => {};
    // Render post...
    var smallimages = item.image.slice(1);
    console.log('smallimages: ', smallimages);

    return (
        <Layout>
            <div key={item._id} className="container mt-3">
                <div className={styles.gridcontainer}>
                    <div>
                        <div>
                            <Image
                                src={item.image[0]}
                                height={400}
                                width={400}
                            />
                        </div>

                        <div>
                            smallimages.map((smallimage)=>
                            <Image src={smallimage} height={50} width={50} />
                            )
                            <Image src={item.image[2]} height={50} width={50} />
                            <Image src={item.image[3]} height={50} width={50} />
                            <Image src={item.image[4]} height={50} width={50} />
                            <Image src={item.image[5]} height={50} width={50} />
                        </div>
                    </div>

                    <div>
                        <h3 className="">{item.title}</h3>
                        <pre className={styles.intro}>
                            {`If you were a teardrop
In my eye 👀 , 
For fear of losing you, I would never cry. 
And if the golden sun, Should cease to shine its light, 
Just one smile from you, Would make my whole world bright.`}
                        </pre>
                        <p className={styles.description}>
                            Price:
                            <span className={styles.price}>
                                {formatNumber(item.price)}
                            </span>
                        </p>
                        <div className="">
                            <div className={styles.inputwraper}>
                                <input
                                    type="text"
                                    ref={inputEL}
                                    onInput={handleCount}
                                    onBlur={handlefocus}
                                    //  onChange={handleCount}
                                    //    className='form-control'
                                    defaultValue="1"
                                    className={styles.input}
                                />

                                <button
                                    className={styles.add}
                                    onClick={() => handleIncrease(item)}
                                    // className='border border-left-0 text-center'
                                    // href=''
                                    disabled={incdis}
                                >
                                    +
                                </button>
                                <button
                                    className={styles.minus}
                                    // className='border border-left-0 border-top-0  text-center'
                                    // href=''
                                    onClick={() => handleDecrease(item)}
                                    disabled={decdis}
                                >
                                    -
                                </button>
                            </div>
                            <button
                                // className={styles.btnaddtocart}
                                className="btn btn-success"
                                onClick={handleCart}
                            >
                                Add to Cart
                            </button>
                        </div>
                    </div>
                </div>
                <div className="row">
                    <ul className="col d-flex mt-3">
                        <button
                            //   className='flex-fill'
                            className={stylestab1}
                            onClick={() => {
                                setStylestab1(styles.tabactive);

                                setStylestab2(styles.tab);

                                setStylestab3(styles.tab);

                                setStylestab4(styles.tab);
                            }}
                        >
                            Comments
                        </button>

                        <button
                            className={stylestab2}
