import Link from 'next/link';
import Image from 'next/image';
import { useEffect, useState } from 'react';
import { useInsDispatch, useIns } from '../pages/contexts/CartContext';
import { useUser } from '../pages/contexts/userContext';
import { usefunctions } from '../pages/contexts/functionContext';
import { formatNumber } from '../utils/utils';

export default function ItemsInIndex({ item }) {
    //const { dispatch } = useInsDispatch();
    const { AddToCart } = usefunctions();
    const [busy, setBusy] = useState(false);
    const [mgvisible, setMgvisible] = useState(false);

    const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch();
    const { itemCount, total, instances } = useIns();

    const user = useUser();
    const handleIncrease = (e) => {
        setBusy(true);
        setMgvisible(true);
        AddToCart(
            1,
            item._id,
            user,

            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        ).then(() => setBusy(false));
    };

    return (
        <>
            <div key={item._id}>
                <div className="card card-body">
                    <Link href="/store/[id]" as={`/store/${item._id}`}>
                        <a>
                            <Image
                                src={`/${item.image}`}
                                height={300}
                                width={300}
                            />
                            <p className="item-name">{item.title}</p>
                        </a>
                    </Link>

                    <p className="text-left">{formatNumber(item.price)}</p>

                    <div className="row ">
                        <div
                            className="col-6"
                            style={{ visibility: mgvisible ? true : false }}
                        >
                            {' '}
                            You have added
                        </div>
                        <button
                            onClick={() => handleIncrease(item)}
                            className="btn btn-outline-primary btn-sm col-6"
                            disabled={busy}
                        >
                            Add To Cart
                        </button>
                        {/*isInCart(product) && (
                <button
                  onClick={() => increase(product)}
                  className='btn btn-outline-primary btn-sm'
                >
                  Add more
                </button>
              )*/}

                        {/*!isInCart(product) && (
                <button
                  onClick={() => addProduct(product)}
                  className='btn btn-primary btn-sm'
                >
                  Add to cart
                </button>
              )*/}
                    </div>
                </div>
            </div>
        </>
    );
}
