import { useState } from 'react'
import Layout from '../../components/layout'

import ToChkInstances from '../../components/ToChkInstances'
import { useUserDispatch, useUser } from '../contexts/userContext'
import { useIns, useInsDispatch } from '../contexts/CartContext'
import { usefunctions } from '../contexts/functionContext'
import { formatNumber } from '../../utils/utils'

export default function ToCheckOut() {
    var temp
    const user = useUser()
    const setUser = useUserDispatch()
    const [orderon, setOrderOn] = useState(false)

    const { instances, itemCount, total, orders } = useIns()
    const {
        setInstance,
        setInsLen,
        setItemCount,
        setTotal,
        setOrders,
    } = useInsDispatch()
    const { checkOut, GetAllOrder } = usefunctions()

    const handleChkOut = () => {
        checkOut(user, setInstance, setInsLen, setItemCount, setTotal)
    }

    const handleOrders = () => {
        GetAllOrder(user, setOrders).then((res) => {
            console.log('orders:  ', orders)
        })
        setOrderOn(true)
    }

    return (
        <Layout>
            {console.log(
                'checked items: ',
                instances &&
                    instances.filter((instance) => instance.check === true)
            )}
            {instances &&
                instances.filter((instance) => instance.check === true).length >
                    0 && (
                    <div className="container">
                        <div className="">You are going to check out: </div>
                        {instances
                            .filter((instance) => instance.check === true)
                            .map((checkedins) => {
                                return (
                                    <ToChkInstances
                                        checkedins={checkedins}
                                        key={checkedins._id}
                                    />
                                )
                            })}
                        <div className="row">
                            <div className="mr-3">Total count: {itemCount}</div>
                            <div className="mr-3">
                                Total price: {formatNumber(total)}
                            </div>
                            <button
                                className="btn btn-success"
                                onClick={handleChkOut}
                            >
                                Confirm
                            </button>
                        </div>
                    </div>
                )}

            {((instances &&
                instances.filter((instance) => instance.check === true)
                    .length === 0) ||
                !instances ||
                instances.length == 0) && <div>No product to check out</div>}
            <button className="btn btn-success" onClick={handleOrders}>
                {' '}
                Your Orders
            </button>
            {orders &&
                orders.map((order) => (
                    <div>
                        <div>{order.item.title}</div>
                        <div>{order.quantity}</div>
                        <div>{order.item.price}</div>
                    </div>
                ))}
        </Layout>
    )
}
