import styles from './about/about.module.css';
import { useRef, useState } from 'react';
export default function About() {
    const divref = useRef(null);
    const imgref = useRef(null);
    const rsltref = useRef(null);
    const [hover, setHover] = useState(false);
    const handleHover = () => {
        setHover(true);
    };

    const handlestatic = () => {
        setHover(false);
    };
    const handleImage = () => {};
    console.log('divref: ', divref);
    console.log('imgref: ', imgref);
    return (
        <div>
            <div class="row" ref={divref}>
                <div class="column" ref={imgref}>
                    <img
                        src="/uploads/0fec1a5414eecd7c7f3213d41204d491"
                        alt="Nature"
                        onclick={handleImage}
                        height={200}
                        width={200}
                    />
                </div>
                <div class="column">
                    <img
                        src="/uploads/1ea5055277b39956f5273440f490b0e6"
                        alt="Snow"
                        onclick="myFunction(this);"
                        height={200}
                        width={200}
                    />
                </div>
                <div class="column">
                    <img
                        src="uploads/6d24b02bfe7e19cccb870933a2126816"
                        alt="Mountains"
                        onclick="myFunction(this);"
                        height={200}
                        width={200}
                    />
                </div>
                <div class="column">
                    <img
                        src="/uploads/7da9c650ff9470b29c3744ec25f40032"
                        alt="Lights"
                        onclick="myFunction(this);"
                        height={200}
                        width={200}
                    />
                </div>
            </div>

            <div class="container">
                <span
                    onclick="this.parentElement.style.display='none'"
                    class="closebtn"
                >
                    &times;
                </span>

                <img id="expandedImg" style={{ width: '100%' }} />

                <div id="imgtext"></div>
            </div>
        </div>
    );
}
