import Link from 'next/link';
import { useInsDispatch, useIns } from '../pages/contexts/CartContext';
import { useUser } from '../pages/contexts/userContext';
import { usefunctions } from '../pages/contexts/functionContext';

export default function ItemsInIndex({ item }) {
  //const { dispatch } = useInsDispatch();
  const { AddToCart, GetAllCarts } = usefunctions();

  const { instances } = useIns();
  const { setInstance, setLen } = useInsDispatch();
  const user = useUser();
  const handleIncrease = (e) => {
    AddToCart(1, item._id, user, setInstance, setLen)
      /* .then(() =>
        dispatch({
          type: 'INCREASE',
        })
      )*/
      .then((result) => {
        setInstance(result);
        const reducer = (accumulator, currentValue) =>
          accumulator + currentValue.quantity;
        var inslen = result ? result.reduce(reducer, 0) : 0;
        setLen(inslen);
        console.log('instances in intensinindex: ', result);
      });

    /* dispatch({
      type: 'INCREASE',
    });*/
  };
  return (
    <>
      <div key={item._id}>
        <div className='card card-body'>
          <img
            src={item.image}
            style={{
              display: 'block',
              margin: '0 auto 10px',
              height: '200px',
              width: '200px',
            }}
          />
          <p className='item-name'>{item.title}</p>

          <h3 className='text-left'>price: {item.price}</h3>

          <div className='text-right'>
            <Link href='/[id]/detail' as={`/${item._id}/detail`}>
              <a className='btn btn-link btn-sm mr-2'>detail</a>
            </Link>

            <button
              onClick={() => handleIncrease(item)}
              className='btn btn-outline-primary btn-sm'
            >
              Add more
            </button>
            {/*isInCart(product) && (
                <button
                  onClick={() => increase(product)}
                  className='btn btn-outline-primary btn-sm'
                >
                  Add more
                </button>
              )*/}

            {/*!isInCart(product) && (
                <button
                  onClick={() => addProduct(product)}
                  className='btn btn-primary btn-sm'
                >
                  Add to cart
                </button>
              )*/}
          </div>
        </div>
      </div>
    </>
  );
}
