import ItemInstance from '../../models/iteminstance';
import dbConnect from '../../utils/dbConnect';

export const config = {
    api: {
        externalResolver: true,
    },
};

export default async function handler(req, res) {
    req.body = JSON.parse(req.body);
    console.log('req.body: ', req.body);
    await dbConnect();

    //https://stackoverflow.com/questions/38439748/best-way-of-chaining-mongoose-queries
    ItemInstance.findByIdAndUpdate(req.body.instanceid, {
        $inc: { quantity: req.body.count },
        buy_date: Date.now(),
        //check: true,
    })
        .then((res) => {
            return ItemInstance.findById(req.body.instanceid);
        })
        .then((res) => {
            return ItemInstance.find({
                user: req.body.user,
                status: 'Cart',
            }).populate('item');
        })
        .then((items_carts) => {
            res.status(201).json({ data: items_carts });
            res.end();
        })
        .catch((err) => {
            console.log('I am in err: ', err);
            res.status(500).json({ error: err });
            res.end();
        });
}
