import { useEffect, useRef, useState } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/router';
import dbConnect from '../../utils/dbConnect';
import Item from '../../models/item';
import Image from 'next/image';
import styles from '../../styles/id.module.css';
import { useInsDispatch } from '../../pages/contexts/CartContext';
import { useUser } from '../pages/contexts/userContext';
import { usefunctions } from '../../pages/contexts/functionContext';

export default function Post({ item }) {
  const router = useRouter();
  const { id } = router.query;
  const inputEL = useRef(null);
  const [incdis, setIncdis] = useState(false);
  const [decdis, setDecdis] = useState(false);
  const [dltdis, setDltdis] = useState(false);

  const user = useUser();
  const { ChangeToCart, ChangeAddCart } = usefunctions();
  const { setInstance, setInsLen } = useInsDispatch();

  var a,
    c = false;
  var oldValue = iteminstance.quantity;
  var oldValueforback = iteminstance.quantity;

  const handleCount = (e) => {
    console.log('oldValueforback: ', oldValueforback);
    console.log('oldValue: ', oldValue);
    if (inputEL.current.validity.valid) {
      if (inputEL.current.value == '') {
        //this is for empty
        c = true;
        a = setTimeout(() => (inputEL.current.value = oldValueforback), 2000);
      } else {
        c = false;
        //this is for number
        if (a) {
          clearTimeout(a);
          a = null;
        }
        if (e.nativeEvent.inputType !== 'deleteContentBackward') {
          oldValueforback = inputEL.current.value;
        }
        oldValue = inputEL.current.value;
      }
    } else {
      //this is for invalid
      if (!c) {
        //previous is number then use oldValue
        inputEL.current.value = oldValue;
        oldValueforback = inputEL.current.value;
      } else {
        //previous is "" then use oldValueforback
        inputEL.current.value = oldValueforback;
        clearTimeout(a);
        console.log('I am in clear timeout below');
        a = null;
        c = false;
        oldValue = inputEL.current.value;
      }
    }
    setIncdis(parseInt(inputEL.current.value) >= 999);
    setDecdis(parseInt(inputEL.current.value) <= 1);
  };

  const handleIncrease = (e) => {
    setIncdis(true);
    setDecdis(true);
    setDltdis(true);
    inputEL.current.value = parseInt(inputEL.current.value) + 1;
    //hanle DB -> plus value
    ChangeAddCart(1, iteminstance._id, user, setInstance, setInsLen).then(
      () => {
        console.log('I am in handleIncrease');
        setIncdis(parseInt(inputEL.current.value) >= 999);
        setDecdis(parseInt(inputEL.current.value) <= 1);
        setDltdis(false);
      }
    );
  };

  const handleDecrease = (e) => {
    setIncdis(true);
    setDecdis(true);
    setDltdis(true);
    inputEL.current.value = parseInt(inputEL.current.value) - 1;
    //hanle DB -> minus value
    ChangeAddCart(-1, iteminstance._id, user, setInstance, setInsLen).then(
      () => {
        console.log('I am in handleDecrease');
        setIncdis(parseInt(inputEL.current.value) >= 999);
        setDecdis(parseInt(inputEL.current.value) <= 1);
        setDltdis(false);
      }
    );
  };

  const handlefocus = () => {
    setIncdis(true);
    setDecdis(true);
    setDltdis(true);
    //handle DB -> change value
    ChangeToCart(
      inputEL.current.value,
      iteminstance._id,
      user,
      setInstance,
      setInsLen
    ).then(() => {
      console.log('I am in handlefocus');
      setIncdis(parseInt(inputEL.current.value) >= 999);
      setDecdis(parseInt(inputEL.current.value) <= 1);
      setDltdis(false);
    });
  };

  useEffect(() => {
    setIncdis(parseInt(inputEL.current.value) >= 999);
    setDecdis(parseInt(inputEL.current.value) <= 1);
    console.log('I am in effect');
  }, [user]);

  // Render post...
  console.log('post: ', item);
  return (
    <>
      <div key={item._id} className='container'>
        <div className='row'>
          <div className='col-4'>
            <Image src={`/${item.image}`} height={400} width={400} />
          </div>

          <div className='col-8'>
            <h3 className='row-2 text-center'>{item.title}</h3>
            <p className='row-3'>Here is some introduce about this item...</p>
            <p className='row-4'>price: {item.price}</p>
            <div className='row'>
              <div className='col-3'>
                <div className='d-flex flex-row '>
                  <input
                    id='id1'
                    type='text'
                    ref={inputEL}
                    onInput={handleCount}
                    onBlur={handlefocus}
                    //  onChange={handleCount}
                    //    className='form-control'
                    style={{ width: '40px' }}
                    defaultValue={item.quantity}
                    className='p-0 border'
                  />
                  <div className='d-flex flex-column m-0'>
                    <a
                      className={styles.add}
                      onClick={() => handleIncrease(item)}
                      // className='border border-left-0 text-center'
                      href=''
                      disabled={incdis}
                    >
                      +
                    </a>
                    <a
                      className={styles.minus}
                      // className='border border-left-0 border-top-0  text-center'
                      href=''
                      onClick={() => handleDecrease(item)}
                      disabled={decdis}
                    >
                      -
                    </a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='row'>
          <div className='col'>col3</div>
          <div className='col'>col4</div>
        </div>
      </div>
    </>
  );
}

export async function getStaticPaths() {
  console.log('I am in [id] getStaticPaths');
  //get items from db like index
  // Call an external API endpoint to get posts
  await dbConnect();

  // find all the data in our database
  const result = await Item.find({});
  const items = JSON.parse(JSON.stringify(result));
  // Get the paths we want to pre-render based on posts
  const paths = items.map((item) => ({ params: { id: item._id } }));

  // We'll pre-render only these paths at build time.
  // { fallback: false } means other routes should 404.
  return {
    paths,
    fallback: false,
    //fallback: true or false // See the "fallback" section below
  };
}

// This also gets called at build time
export async function getStaticProps({ params }) {
  console.log('I am in [id] getStaticProps');
  // params contains the post `id`.
  // If the route is like /posts/1, then params.id is 1

  await dbConnect();

  // find all the data in our database
  const result = await Item.findById(params.id);
  const item = JSON.parse(JSON.stringify(result));

  // Pass post data to the page via props
  return { props: { item } };
}
