import Layout from '../../components/layout'
import ToChkInstances from '../../components/ToChkInstances'
import { useUserDispatch, useUser } from '../contexts/userContext'
import { useIns, useInsDispatch } from '../contexts/CartContext'
import { usefunctions } from '../contexts/functionContext'
import { formatNumber } from '../../utils/utils'

export default function ToCheckOut() {
    const user = useUser()

    const { instances, itemCount, total } = useIns()
    const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch()
    const { checkOut } = usefunctions()

    const handleChkOut = () => {
        checkOut(user, setInstance, setInsLen, setItemCount, setTotal)
    }

    return (
        <Layout>
            {instances &&
                instances.filter((instance) => instance.check === true).length >
                    0 && (
                    <div className="container">
                        <div className="">You are going to check out: </div>
                        {instances &&
                            instances
                                .filter((instance) => instance.check === true)
                                .map((checkedins) => {
                                    return (
                                        <ToChkInstances
                                            checkedins={checkedins}
                                            key={checkedins._id}
                                        />
                                    )
                                })}
                        <div className="row">
                            <div className="mr-3">Total count: {itemCount}</div>
                            <div className="mr-3">
                                Total price: {formatNumber(total)}
                            </div>
                            <button
                                className="btn btn-success"
                                onClick={handleChkOut}
                            >
                                Confirm
                            </button>
                        </div>
                    </div>
                )}
            {((instances &&
                instances.filter((instance) => instance.check === true)
                    .length === 0) ||
                !instances ||
                instances.length == 0) && <div>No product to check out</div>}
        </Layout>
    )
}
