import Layout from '../../../components/layout';
import { useRouter } from 'next/router';

export default function Comment() {
    const router = useRouter();
    const ins = JSON.parse(router.query.ins);
    console.log('router: ', router);
    console.log('ins: ', ins);
    const handleSubmit = () => {
        console.log('hello');
    };
    return (
        <Layout>
            <div className="container mt-3 ">
                <div>
                    <img src={`/${ins.item.image}`} height={70} width={70} />
                </div>
                <div>{ins.item.title}</div>
                <form onSubmit={handleSubmit}>
                    <label>Comment:</label>
                    <textarea
                        className="form-control"
                        rows="5"
                        id="comment"
                    ></textarea>
                    <div className="custom-file">
                        <label className="custom-file-label">
                            <input
                                type="file"
                                className="file-control-file"
                                id="customFile"
                            />
                            Choose file
                        </label>
                    </div>
                    <button className="btn btn-success mr-3">submit</button>
                    <button className="btn btn-success mr-3">cancel</button>
                </form>
            </div>
        </Layout>
    );
}
