import '../styles/globals.css';
import Link from 'next/link';
import Head from 'next/head';
//import App from 'next/app';
function MyApp({ Component, pageProps, title = 'This is the default title' }) {
  //return <Component {...pageProps} />;
  //}

  return (
    <div>
      <CounterProvider>
        <Head>
          <title>{title}</title>
          <meta charSet='utf-8' />
          <meta
            name='viewport'
            content='initial-scale=1.0, width=device-width'
          />
        </Head>
        {/*
    <Header />*/}
        <header className='sticky-top'>
          <div>
            <nav className='navbar navbar-expand-sm bg-dark navbar-dark sticky-top'>
              <div className='col-sm-9'>
                <ul className='navbar-nav'>
                  <li className='nav-item'>
                    <Link href='/'>
                      <a className='navbar-brand'>Home</a>
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link href='/item/display/Items'>
                      <a className='nav-link'> Display Items(user only)</a>
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link href='/link2'>
                      <a className='nav-link'> Display Genre</a>
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link href='/about'>
                      <a className='nav-link'>About</a>
                    </Link>
                  </li>
                  <li className='nav-item'>
                    <Link href='/contact'>
                      <a className='nav-link'>Contact</a>
                    </Link>
                  </li>
                </ul>
              </div>
              <div className='mr-4 ml-2 col-sm-3'>
                <Link href='/'>
                  <a className='text-success mt-1 mb-0 mr-2'>Store</a>
                </Link>
                <Link href='/about'>
                  <a className='text-success mt-1 mb-0 mr-2'>About</a>
                </Link>
                <Link href='/cart'>
                  <a className='text-light mt-1 mb-0'> Cart ()</a>
                </Link>
              </div>
            </nav>
          </div>
        </header>

        <Component {...pageProps} />

        <footer>
          <nav className='navbar navbar-expand-sm shadow'>
            <ul className='navbar-nav'>
              <li className='nav-item'>
                <Link href='/'>
                  <a className='nav-link py-0'>Contact Us</a>
                </Link>
              </li>
            </ul>
          </nav>
        </footer>
      </CounterProvider>
    </div>
  );
}
