import Head from 'next/head';
import styles from '../styles/Home.module.css';
import dbConnect from '../utils/dbConnect';
import Layout from '../components/layout';
import Item from '../models/Item';

export default function Index({ items }) {
  return (
    <Layout>
      {console.log('I am in index')}
      {/* Create a card for each item */}
      {items.map((item) => (
        <div key={item._id}>
          <div className='card'>
            <img src={item.image_url} />
            <h5 className='item-name'>{item.title}</h5>
            <div className='main-content'>
              <p className='item-name'>{item.tile}</p>
              <p className='price'>Owner: {item.price}</p>

              <div className='btn-container'>
                <Link href='/[id]/edit' as={`/${item._id}/edit`}>
                  <button className='btn edit'>Edit</button>
                </Link>
                <Link href='/[id]' as={`/${item._id}`}>
                  <button className='btn view'>View</button>
                </Link>
              </div>
            </div>
          </div>
        </div>
      ))}
    </Layout>
  );
}
/* Retrieves item(s) data from mongodb database */
export async function getServerSideProps() {
  await dbConnect();

  /* find all the data in our database */
  const result = await Item.find({});
  const items = result.map((doc) => {
    const item = doc.toObject();
    item._id = item._id.toString();
    return item;
  });

  console.log('items: ', items);
  return { props: { items: items } };
}
