import Layout from '../../../components/layout';
import { useRouter } from 'next/router';
import { useRef } from 'react';

import { usefunctions } from '../../contexts/functionContext';

export default function Comment() {
    const textEL = useRef(null);
    const imgEL = useRef(null);
    const formEL = useRef(null);
    const { PostComment } = usefunctions();
    const router = useRouter();
    const ins = JSON.parse(router.query.ins);
    console.log('router: ', router);
    console.log('ins: ', ins);

    const handleSubmit = (e) => {
        e.preventDefault();
        // PostComment(ins._id, textEL.current.value);
        /*
        var text = textEL.current.value;
        var file = [];
        for (let i = 0; i < imgEL.current.files.length; i++) {
            file.push(imgEL.current.files[i]);
        }*/

        const formData = new FormData();
        formData.append('file', file);
        formData.append('text', text);
        formData.append('id', ins._id);

        PostComment(formData);
    };

    const handleCancel = (e) => {
        e.preventDefault();
        console.log('cancel');
        router.push('/order');
    };

    const handleFile = (e) => {
        const formData = new FormData();

        // Update the formData object
        formData.append('name', 'test');
        formData.append('myFile', e.target.files[0]);
        console.log('e.target.files[0]: ', e.target.files[0]);
        // Details of the uploaded file
        console.log('formData.getAll(myFile); ', formData.getAll('myFile'));
    };
    // Create an object of formData

    return (
        <Layout>
            <div className="container mt-3 ">
                <div>
                    <img src={`/${ins.item.image}`} height={70} width={70} />
                </div>
                <div>{ins.item.title}</div>
                <form
                    ref={formEL}
                    onSubmit={handleSubmit}
                    // action="/api/user_comment_post"
                    method="post"
                    encType="multipart/form-data"
                >
                    <label>Comment:</label>
                    <textarea
                        name="textarea"
                        ref={textEL}
                        className="form-control"
                        rows="5"
                    ></textarea>
                    {/*
                    <input name="insid" value={ins._id} hidden readOnly />*/}
                    <div className="custom-file">
                        <label className="custom-file-label">
                            <input
                                name="commentattach"
                                ref={imgEL}
                                type="file"
                                className="file-control-file"
                                //   onChange={handleFile}
                                multiple="multiple"
                            />
                            Choose file
                        </label>
                    </div>
                    <button className="btn btn-success mr-3">submit</button>
                    <button
                        className="btn btn-success mr-3"
                        onClick={handleCancel}
                    >
                        cancel
                    </button>
                </form>
            </div>
        </Layout>
    );
}
