import Layout from '../components/layout';
import CartInstances from '../components/CartInstances';
import { useUser } from './contexts/userContext';
import { useIns, useInsDispatch } from './contexts/CartContext';
import { usefunctions } from './contexts/functionContext';
import { useEffect } from 'react';

export default function Cart() {
  const user = useUser();
  const i = 1;
  const { instances } = useIns();
  const { setInstance } = useInsDispatch();
  const { GetAllCarts } = usefunctions();

  useEffect(() => {
    GetAllCarts(user);
    // Update the document title using the browser API
    fetch('api/user_getallcart_post', {
      method: 'post',
      body: JSON.stringify({
        user: user,
      }),
    })
      .then((response) => response.json())
      .then((result) => {
        setInstance(result.data);
      })
      .catch(new Error());
  });

  return (
    <Layout>
      {/* Create a card for each item */}
      <div className='card-columns'>
        {instances &&
          instances.map((iteminstance) => {
            return (
              <CartInstances
                iteminstance={iteminstance}
                key={iteminstance._id}
              />
            );
          })}
        {!instances && <div>No items</div>}
      </div>
    </Layout>
  );
}
