import ItemInstance from '../../models/iteminstance';
import Item from '../../models/item';
import dbConnect from '../../utils/dbConnect';

export const config = {
    api: {
        externalResolver: true,
    },
};

export default async function handler(req, res) {
    //req.body = JSON.parse(req.body);
    console.log('req.body: ', req.body);
    await dbConnect();

    /* var iteminstance = new ItemInstance({
        item: '5fe00ed13f144c7e71c5e1e7',
        user: 'asdfghjk',
        status: 'Cart',
        quantity: 99,
    });*/

    Item.findByIdAndUpdate('5fe00e913f144c7e71c5e1e5', {
        image: [
            '/item/andreas-weiland-TMpqNhMunQY-unsplash.jpg',
            '/item/brooke-lark-8beTH4VkhLI-unsplash.jpg',
            '/item/faria-anzum-ONK9IlKizS4-unsplash.jpg',
            '/item/ian-dooley-TLD6iCOlyb0-unsplash.jpg',
            '/item/jonatan-pie-xgTMSz6kegE-unsplash.jpg',
            '/item/sheri-silver-5A0O12BIsjY-unsplash.jpg',
        ],
    })
        .then((item) => {
            console.log('result after update  item: ', item);
            res.status(201).json({ data: item });
            res.end();
        })
        .catch((err) => {
            res.status(500).json({ error: err });
            res.end();
        });
}

