import Head from 'next/head';
import Link from 'next/link';
import { useIns, useInsDispatch } from '../pages/contexts/CartContext';

import { useUser, useUserDispatch } from '../pages/contexts/userContext';

import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';

export default function Layout({
    children,
    title = 'This is the default title',
}) {
    const { inslen } = useIns();
    const { user, tmpuser } = useUser();
    const { setInstances } = useInsDispatch();
    const { setUser } = useUserDispatch();
    const handleLogout = () => {
        setUser('');
        setInstances('');
    };
    return (
        <div>
            <Head>
                <title>{title}</title>
                <meta charSet="utf-8" />
                <meta
                    name="viewport"
                    content="initial-scale=1.0, width=device-width"
                />
            </Head>
            <Navbar bg="light" expand="lg" sticky="top">
                <Navbar.Brand href="/">Home</Navbar.Brand>

                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Link href="/about">About</Link>
                        <Link href="/">Contact</Link>
                    </Nav>
                    <Nav>
                        {user && (
                            <Link href="/">
                                <a className="text-success mt-1 mb-0 mr-2">
                                    Hello ({user})
                                </a>
                            </Link>
                        )}
                        {!user && (
                            <Link href="/">
                                <a className="text-success mt-1 mb-0 mr-2">
                                    Hello ({tmpuser})
                                </a>
                            </Link>
                        )}
                        {console.log('user in layout: ', user)}
                        {!user && (
                            <Link href="/login">
                                <a>Login</a>
                            </Link>
                        )}
                        <a
                            className="text-success mt-1 mb-0 mr-2"
                            style={{ cursor: 'pointer' }}
                            onClick={handleLogout}
                        >
                            Logout
                        </a>
                        <Link href="/cart">
                            <a>Cart ({inslen})</a>
                        </Link>
                        <Link href="/order">
                            <a>order</a>
                        </Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>

            {children}
        </div>
    );
}
