import Head from 'next/head';
import Link from 'next/link';
import { useIns, useInsDispatch } from '../pages/contexts/CartContext';

import { useUser, useUserDispatch } from '../pages/contexts/userContext';

import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
export default function Layout({
    children,
    title = 'This is the default title',
}) {
    const { inslen } = useIns();
    const { user, tmpuser } = useUser();
    const { setInstances } = useInsDispatch();
    const { setUser } = useUserDispatch();
    const handleLogout = () => {
        setUser('');
        setInstances('');
    };
    return (
        <div>
            <Head>
                <title>{title}</title>
                <meta charSet="utf-8" />
                <meta
                    name="viewport"
                    content="initial-scale=1.0, width=device-width"
                />
            </Head>
            <Container>
                <Navbar bg="light" expand="lg" sticky="top">
                    <Navbar.Brand>
                        <Link href="/about">
                            <a className="nav-link">About</a>
                        </Link>
                    </Navbar.Brand>

                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto">
                            <Link href="/about">
                                <a className="nav-link">About</a>
                            </Link>
                            <Link href="/contact">
                                <a className="nav-link">Contact</a>
                            </Link>
                        </Nav>
                        <Nav>
                            {!user && (
                                <NavDropdown
                                    title={`Hello ${tmpuser}`}
                                    id="collasible-nav-dropdown"
                                >
                                    <NavDropdown.Item>
                                        <Link href="/login">
                                            <a>Login</a>
                                        </Link>
                                    </NavDropdown.Item>
                                    <NavDropdown.Item>
                                        <Link href="/register">
                                            <a>New? register</a>
                                        </Link>
                                    </NavDropdown.Item>
                                    <NavDropdown.Divider />

                                    <NavDropdown.Item>
                                        <Link href="/order">
                                            <a>Order</a>
                                        </Link>
                                    </NavDropdown.Item>
                                </NavDropdown>
                            )}
                            {user && (
                                <NavDropdown
                                    title={`Hello ${user}`}
                                    id="collasible-nav-dropdown"
                                >
                                    <NavDropdown.Item>
                                        <Link href="/order">
                                            <a>Order</a>
                                        </Link>
                                    </NavDropdown.Item>

                                    <NavDropdown.Divider />
                                    <NavDropdown.Item>
                                        <a
                                            style={{ cursor: 'pointer' }}
                                            onClick={handleLogout}
                                        >
                                            Logout
                                        </a>
                                    </NavDropdown.Item>
                                </NavDropdown>
                            )}

                            <Link href="/cart">
                                <a className="nav-link">Cart ({inslen})</a>
                            </Link>
                        </Nav>
                    </Navbar.Collapse>
                </Navbar>
            </Container>
            {children}
        </div>
    );
}
