import { useEffect } from 'react';
import Link from 'next/link';
import Image from 'next';
import Layout from '../components/layout';
import CartInstances from '../components/CartInstances';
import { useUserDispatch, useUser } from './contexts/userContext';
import { useIns, useInsDispatch } from './contexts/CartContext';
import { usefunctions } from './contexts/functionContext';
import { useInitial, useInitialDispatch } from './contexts/initialContext';
import { formatNumber } from '../utils/utils';

export default function Order() {
    var temp;
    const user = useUser();
    const setUser = useUserDispatch();

    const { instances, itemCount, total, orders } = useIns();
    const {
        setInstance,
        setInsLen,
        setItemCount,
        setTotal,
        setOrders,
    } = useInsDispatch();
    const { GetAllCarts, checkdb, toCheckOut, GetAllOrder } = usefunctions();

    const setInitial = useInitialDispatch();
    const inital = useInitial();

    useEffect(() => {
        user && GetAllOrder(user, setOrders);
    }, [orders]);

    return (
        <Layout>
            <div className="container">
                <div> Your orders</div>
                {orders &&
                    orders.map((order) => {
                        return (
                            <div key={order._id} className="row">
                                <div>
                                    <Link
                                        href="/store/[id]"
                                        as={`store/${order.item._id}`}
                                    >
                                        <a className="btn btn-link btn-sm mr-2">
                                            {' '}
                                            <img
                                                src={`/${order.item.image}`}
                                                height={70}
                                                width={70}
                                            />
                                        </a>
                                    </Link>
                                </div>
                                <div className="align-self-center mr-2">
                                    <Link
                                        href="/store/[id]"
                                        as={`store/${order.item._id}`}
                                    >
                                        <a className="btn btn-link btn-sm  ">
                                            {order.item.title}
                                        </a>
                                    </Link>
                                </div>
                                <div className=" align-self-center mr-2">
                                    unit price: {formatNumber(order.item.price)}
                                </div>

                                <div className="align-self-center mr-3">
                                    quantity: {order.quantity}
                                </div>

                                <Link
                                    href="/[id]/comment"
                                    as={`${order.item._id}/comment`}
                                >
                                    <a className="align-self-center mr-3">
                                        comment
                                    </a>
                                </Link>
                            </div>
                        );
                    })}
            </div>
        </Layout>
    );
}
