//import Head from 'next/head';
//import styles from '../styles/Home.module.css';

import dbConnect from '../utils/dbConnect';
import Layout from '../components/layout';
import Item from '../models/item';
import ItemsInIndex from '../components/itemsInIndex';
import { useItems, usedispatch } from './contexts/ProductsContext';
import { useUserDispatch, useUser } from './contexts/userContext';
import { useEffect } from 'react';

export default function Index({ items }) {
  var temp;
  const user = useUser();
  const setUser = useUserDispatch();

  const state = useItems();
  const dispatch = usedispatch();

  const getAllPriducts = (event) =>
    dispatch({
      type: 'GETALL',
      payload: items,
    });

  useEffect(() => {
    getAllPriducts();
  }, [items]);

  useEffect(() => {
    if (sessionStorage.getItem('key')) {
      temp = sessionStorage.getItem('key');
    } else {
      temp = Math.random()
        .toString(36)
        .replace(/[^a-z]+/g, '')
        .substr(0, 12);
      sessionStorage.setItem('key', temp);
    }
    setUser(temp);
  });
  return (
    <Layout>
      {/* Create a card for each item */}
      <div className='card-columns'>
        {items.map((item) => (
          <ItemsInIndex item={item} key={item._id} />
        ))}
      </div>
    </Layout>
  );
}
/* Retrieves item(s) data from mongodb database  */
export async function getStaticProps() {
  //export async function getServerSideProps() {
  await dbConnect();

  // find all the data in our database
  const result = await Item.find({});
  const items = JSON.parse(JSON.stringify(result));

  return { props: { items: items } };
}
