                            <div key={order._id}>
                                <div className={styles.ordertitle}>
                                    checkout time :{' '}
                                    {new Date(order.buy_date).toDateString()}{' '}
                                    {order.user}
                                    {order.ordernumber}
                                </div>
                                <div className="row" key={order._id}>
                                    <div className="col-sm-2">
                                        <Link
                                            href="/store/[id]"
                                            as={`store/${order.item._id}`}
                                        >
                                            <a className="btn btn-link btn-sm mr-2">
                                                <Image
                                                    src={order.item.image[0]}
                                                    height={70}
                                                    width={70}
                                                />
                                            </a>
                                        </Link>
                                    </div>
                                    <div className="col-sm-1">
                                        <Link
                                            href="/store/[id]"
                                            as={`store/${order.item._id}`}
                                        >
                                            <a className={styles.link}>
                                                {order.item.title}
                                            </a>
                                        </Link>
                                    </div>
                                    <div className="col-sm-2">
                                        <span className={styles.key}>
                                            unit price{' '}
                                        </span>
                                        {formatNumber(order.item.price)}
                                    </div>
                                    <div className="col-sm-2">
                                        <span className={styles.key}>
                                            quantity
                                        </span>{' '}
                                        {order.quantity}
                                    </div>
                                    <div className="col-sm-2">
                                        <span className={styles.key}>
                                            price{' '}
                                        </span>
                                        {formatNumber(
                                            order.item.price * order.quantity
                                        )}
                                    </div>
                                    <div className="col-sm-2">
                                        <span className={styles.key}>
                                            comment
                                        </span>
                                        <div className={styles.commentwrap}>
                                            {order.commenttext}
                                        </div>
                                        {order.commentimage && (
                                            <div>
                                                {order.commentimage.map(
                                                    (imgsrc) => (
                                                        <Image
                                                            key={imgsrc}
                                                            src={imgsrc}
                                                            height={50}
                                                            width={50}
                                                            className="mr-3"
                                                        />
                                                    )
                                                )}
                                            </div>
                                        )}
                                        <Link
                                            href={{
                                                pathname: '/store/[id]/comment',
                                                query: {
                                                    ins: JSON.stringify(order),
                                                },
                                            }}
                                            as={`store/${order.item._id}/comment`}
                                        >
                                            <a className={styles.des}>
                                                <div>
                                                    {order.commenttext &&
                                                        'update'}
                                                    {!order.commenttext &&
                                                        'write comment'}
                                                </div>
                                            </a>
                                        </Link>
                                        <div
                                            className={
                                                cmtmsg === order._id
                                                    ? styles.fadein
                                                    : styles.fadeout
                                            }
                                        >
                                            comment changed!
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ));*/}
            </div>
        </Layout>
    );
}
