import styles from './about/about.module.css';
import { useRef, useState } from 'react';
export default function About() {
    const divref = useRef(null);
    const imgref = useRef(null);
    const rsltref = useRef(null);
    const [hover, setHover] = useState(false);
    const handleHover = () => {
        setHover(true);
    };

    const handlestatic = () => {
        setHover(false);
    };
    return (
        <div>
            <div class="row">
                <div class="column">
                    <img
                        src="img_nature.jpg"
                        alt="Nature"
                        onclick="myFunction(this);"
                    />
                </div>
                <div class="column">
                    <img
                        src="img_snow.jpg"
                        alt="Snow"
                        onclick="myFunction(this);"
                    />
                </div>
                <div class="column">
                    <img
                        src="img_mountains.jpg"
                        alt="Mountains"
                        onclick="myFunction(this);"
                    />
                </div>
                <div class="column">
                    <img
                        src="img_lights.jpg"
                        alt="Lights"
                        onclick="myFunction(this);"
                    />
                </div>
            </div>

            <div class="container">
                <span
                    onclick="this.parentElement.style.display='none'"
                    class="closebtn"
                >
                    &times;
                </span>

                <img id="expandedImg" style="width:100%" />

                <div id="imgtext"></div>
            </div>
        </div>
    );
}
