import { useEffect } from 'react';
import Link from "next/link"
import Layout from '../../components/layout';
import ToChkInstances from '../../components/ToChkInstances';
import { useUserDispatch, useUser } from '../contexts/userContext';
import { useIns, useInsDispatch } from '../contexts/CartContext';
import { usefunctions } from '../contexts/functionContext';
//import { useInitial, useInitialDispatch } from './contexts/initialContext';
import { formatNumber } from '../../utils/utils';

export default function ToCheckOut() {
  var temp;
  const user = useUser();
  const setUser = useUserDispatch();

  const { instances, itemCount, total } = useIns();
  const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch();
  const { GetAllCarts, checkdb, checkOut } = usefunctions();



  const handleChkOut = () => {
    checkOut(user, setInstance, setInsLen,setItemCount, setTotal);
  };

  return (
    <Layout>


        {instances &&
          instances.filter((instance)=>instance.check===true).map((checkedins) => {
            return (
                <div className='container'>
                <div className="">You are going to check out: </div>
              <ToChkInstances     
                checkedins={checkedins}
                key={checkedins._id}
              />
              <div className='row'>
        <div className='mr-3'>Total count: {itemCount}</div>
          <div className='mr-3'>Total price: {formatNumber(total)}</div>
          <button className="btn btn-success" onClick={handleChkOut}> Confirm </button>
        </div>
        </div>
            );
          })}
        {(!instances || instances.length == 0) && <div>No product to check out</div>}



    </Layout>
  );
}
