import { useEffect } from 'react';
import styles from '../styles/order.module.css';
import Link from 'next/link';
import Image from 'next';
import Layout from '../components/layout';
import Comment from '../components/Comment';
import CartInstances from '../components/CartInstances';
import { useUserDispatch, useUser } from './contexts/userContext';
import { useIns, useInsDispatch } from './contexts/CartContext';
import { usefunctions } from './contexts/functionContext';
import { useInitial, useInitialDispatch } from './contexts/initialContext';
import { formatNumber } from '../utils/utils';

export default function Order() {
    var temp;
    const user = useUser();
    const setUser = useUserDispatch();

    const { instances, itemCount, total, orders, chkmsg } = useIns();
    const {
        setInstance,
        setInsLen,
        setItemCount,
        setTotal,
        setOrders,
    } = useInsDispatch();
    const { GetAllCarts, checkdb, toCheckOut, GetAllOrder } = usefunctions();

    const setInitial = useInitialDispatch();
    const inital = useInitial();

    useEffect(() => {
        user && GetAllOrder(user, setOrders);
    }, [orders]);

    return (
        <Layout>
            <div className="row">
                {chkmsg && (
                    <div className="alert alert-success col-2">{chkmsg}</div>
                )}
            </div>
            <div className="container mt-3">
                <div className="row">
                    {orders && (
                        <table className="table">
                            <caption>You orders</caption>
                            <thead className="thead-light">
                                <tr>
                                    <th>Your orders</th>
                                    <th>name</th>
                                    <th>unit price</th>
                                    <th>quantity</th>
                                    <th>comment</th>
                                </tr>
                            </thead>
                            <tbody>
                                {orders.map((order) => {
                                    return (
                                        <tr key={order._id}>
                                            <td style={{ width: '20%' }}>
                                                <Link
                                                    href="/store/[id]"
                                                    as={`store/${order.item._id}`}
                                                >
                                                    <a className="btn btn-link btn-sm mr-2">
                                                        <img
                                                            src={`/${order.item.image}`}
                                                            height={70}
                                                            width={70}
                                                        />
                                                    </a>
                                                </Link>
                                            </td>
                                            <td style={{ width: '15%' }}>
                                                <Link
                                                    href="/store/[id]"
                                                    as={`store/${order.item._id}`}
                                                >
                                                    <a className="btn btn-link btn-sm">
                                                        {order.item.title}
                                                    </a>
                                                </Link>
                                            </td>
                                            <td style={{ width: '15%' }}>
                                                {formatNumber(order.item.price)}
                                            </td>
                                            <td style={{ width: '15%' }}>
                                                {order.quantity}
                                            </td>
                                            <td
                                                style={{ width: '35%' }}
                                                className={styles.comment}
                                            >
                                                {order.commenttext}
                                                <Link
                                                    href={{
                                                        pathname:
                                                            '/store/[id]/comment',
                                                        query: {
                                                            ins: JSON.stringify(
                                                                order
                                                            ),
                                                        },
                                                    }}
                                                    as={`store/${order.item._id}/comment`}
                                                >
                                                    <a>
                                                        {order.commenttext && (
                                                            <button className="ml-3 btn btn-light btn-sm ">
                                                                update
                                                            </button>
                                                        )}
                                                        {!order.commenttext &&
                                                            'Comment'}
                                                    </a>
                                                </Link>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    )}
                </div>
            </div>
        </Layout>
    );
}
