import React, { createContext, useContext } from 'react';

export const FunctionContext = createContext();

const PostComment = (formData, fn) => {
    return (
        fetch('/api/user_comment_post', {
            method: 'post',
            body: formData,
        })
            .then((response) => response.json())
            .then((result) => {
                if (result.status === 500) {
                    fn(result.error);
                } else {
                    fn([]);
                }
                return result;
            })
            // .then((res) => fno(res))
            .catch((err) => new Error(err))
    );
};
const GetAllOrder = (parmsuser, fno) => {
    return fetch('/api/user_order_post', {
        method: 'post',
        body: JSON.stringify({
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => result.data)
        .then((res) => fno(res))
        .catch(new Error());
};
const checkOut = (parmsuser, fnins, fnlen, fnc, fnt) => {
    return fetch('/api/user_chckout_post', {
        method: 'post',
        body: JSON.stringify({
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => {
            fnins(result.data);

            const reducer = (accumulator, currentValue) =>
                accumulator + currentValue.quantity;
            var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
            fnlen(inslen);

            var { itemCount, total } = sumItems(result.data);
            fnc(itemCount);
            fnt(total);
        })
        .catch(new Error());
};

const sumItems = (instances) => {
    var checkedInstances = instances.filter(
        (instance) => instance.check === true
    );

    let itemCount = checkedInstances.reduce(
        (total, checkedInstance) => total + checkedInstance.quantity,
        0
    );
    let total = checkedInstances
        .reduce(
            (total, checkedInstance) =>
                total + checkedInstance.item.price * checkedInstance.quantity,
            0
        )
        .toFixed(2);
    console.log('itemCount in sunItems: ', itemCount);
    return { itemCount, total };
};
const ChgChk = (parmscheck, parmsid, parmsuser, fnins, fnlen, fnc, fnt) => {
    return fetch('/api/user_chkchg_post', {
        method: 'post',
        body: JSON.stringify({
            check: parmscheck,
            instanceid: parmsid,
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => {
            fnins(result.data);

            const reducer = (accumulator, currentValue) =>
                accumulator + currentValue.quantity;
            var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
            fnlen(inslen);
            var { itemCount, total } = sumItems(result.data);
            fnc(itemCount);
            fnt(total);
        })
        .catch(new Error());
};
const DeleteIns = (parmsid, parmsuser, fnins, fnlen, fnc, fnt) => {
    return fetch('/api/user_deleteins_post', {
        method: 'post',
        body: JSON.stringify({
            instanceid: parmsid,
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => {
            fnins(result.data);
            const reducer = (accumulator, currentValue) =>
                accumulator + currentValue.quantity;
            var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
            fnlen(inslen);

            var { itemCount, total } = sumItems(result.data);
            fnc(itemCount);
            fnt(total);

            console.log('inslen in GetAllCartst: ', inslen);
        })
        .catch(new Error());
};

const ChangeAddCart = (
    parmscount,
    parmsid,
    parmsuser,
    oldins_cart,
    fnins,
    fnlen,
    fnc,
    fnt
) => {
    return fetch('/api/user_changeAddcart_post', {
        method: 'post',
        body: JSON.stringify({
            count: parmscount,
            instanceid: parmsid,
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => {
            var newins = result.data;
            var newins_cart = oldins_cart.filter(
                (oldins) => oldins._id !== newins._id
            );
            newins_cart.unshift(newins);

            fnins(newins_cart);

            const reducer = (accumulator, currentValue) =>
                accumulator + currentValue.quantity;
            var inslen = newins.reduce(reducer, 0);
            fnlen(inslen);
            console.log('inslen in GetAllCartst: ', inslen);

            var { itemCount, total } = sumItems(newins);
            fnc(itemCount);
            fnt(total);
        })
        .catch(new Error());
};

const ChangeToCart = (
    parmscount,
    parmsid,
    parmsuser,
    oldins_cart,
    fnins,
    fnlen,
    fnc,
    fnt
) => {
    return fetch('/api/user_changetocart_post', {
        method: 'post',
        body: JSON.stringify({
            count: parmscount,
            instanceid: parmsid,
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => {
            // result is newins
            var newins = result.data;
            var newins_cart = oldins_cart.filter(
                (oldins) => oldins._id !== newins._id
            );
            newins_cart.unshift(newins);

            fnins(newins_cart);

            const reducer = (accumulator, currentValue) =>
                accumulator + currentValue.quantity;
            var inslen = newins_cart.reduce(reducer, 0);
            fnlen(inslen);
            console.log('inslen in GetAllCartst: ', inslen);
            var { itemCount, total } = sumItems(newins_cart);
            fnc(itemCount);
            fnt(total);
        })
        .catch(new Error());
};
const checkdb = () => {
    return fetch('/api/del_carts_all')
        .then(console.log('delete success in function'))
        .catch((err) => new Error(err));
};
const GetAllCarts = (parmsuser, fnins, fnlen, fnc, fnt) => {
    if (!parmsuser) return Promise.reject('no user');
    return fetch('/api/user_getallcart_post', {
        method: 'post',
        body: JSON.stringify({
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => {
            fnins(result.data);

            const reducer = (accumulator, currentValue) =>
                accumulator + currentValue.quantity;
            var inslen = result.data ? result.data.reduce(reducer, 0) : 0;

            fnlen(inslen);

            var { itemCount, total } = sumItems(result.data);
            fnc(itemCount);
            fnt(total);
            console.log('count: ', itemCount);
            console.log('total: ', total);
        })
        .catch(new Error());
};

const AddToCart = (
    parmscount,
    parmsid,
    parmsuser,
    oldins_cart,
    fnins,
    fnlen,
    fnc,
    fnt
) => {
    return fetch('/api/user_addtocart_post', {
        method: 'post',
        body: JSON.stringify({
            count: parmscount,
            itemid: parmsid,
            user: parmsuser,
        }),
    })
        .then((response) => response.json())
        .then((result) => {
            var newins = result.data;
            // now the result is new iteminstance
            var newins_cart = oldins_cart.filter(
                (oldins) => oldins._id !== newins._id
            );
            newins_cart.unshift(newins);

            fnins(newins_cart);
            console.log('newins_cart: ', newins_cart);
            const reducer = (accumulator, currentValue) =>
                accumulator + currentValue.quantity;
            var inslen = newins_cart.reduce(reducer, 0);
            fnlen(inslen);

            var { itemCount, total } = sumItems(newins_cart);
            fnc(itemCount);
            fnt(total);

            console.log('inslen in GetAllCartst: ', inslen);
        })
        .catch(new Error());
};

const FunctionContextProvider = ({ children }) => {
    return (
        <FunctionContext.Provider
            value={{
                GetAllCarts,
                AddToCart,
                checkdb,
                ChangeToCart,
                ChangeAddCart,
                DeleteIns,
                ChgChk,
                checkOut,
                GetAllOrder,
                PostComment,
            }}
        >
            {children}
        </FunctionContext.Provider>
    );
};

export const usefunctions = () => useContext(FunctionContext);
export default FunctionContextProvider;
