import React, { createContext, useContext, useReducer } from 'react';
import { useUser, useUserDispatch } from '../contexts/userContext';

export const CartContextCount = createContext();
export const CartContextActionC = createContext();
export const CartContext = createContext();
export const CartContextAction = createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case 'INCREASE': {
      fetch('/api/user_addtocart_post', {
        method: 'post',
        body: JSON.stringify({
          count: 1,
          itemid: action.payload._id,
          user: action.user,
        }),
      })
        .then((response) => response.json())
        .then((data) => {
          console.log('data: ', data);
        });
      console.log('state: in CartContext', state);
      return state + 1;
    }

    case 'DECREASE':
      return state - 1;
    case 'INCREASE_BY':
      return JSON.parse(JSON.stringify(action.payload));
    default:
      throw new Error(`Unknown action: ${action.type}`);
  }
};

const reducer1 = (state, action) => {
  switch (action.type) {
    case 'GETALL': {
      fetch('api/user_getallcart_post', {
        method: 'post',
        body: JSON.stringify({
          user: action.user,
        }),
      })
        .then((response) => response.json())
        .then((response) => {
          return response.data;
        });
      return;
    }
    default:
      throw new Error(`Unknown action: ${action.type}`);
  }
};

const CartContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, 0);
  const [state1, dispatch1] = useReducer(reducer1, []);
  return (
    <CartContextCount.Provider value={state}>
      <CartContextActionC.Provider value={dispatch}>
        <CartContext.Provider value={state1}>
          <CartContextAction.Provider value={dispatch1}>
            {children}
          </CartContextAction.Provider>
        </CartContext.Provider>
      </CartContextActionC.Provider>
    </CartContextCount.Provider>
  );
};

export const useInstancelength = () => useContext(CartContext);
export const useInstanceDispatch = () => useContext(CartContext1);
export const useInstance = () => useContext(CartContext2);
export default CartContextProvider;
