import Layout from '../../components/layout';
import ToChkInstances from '../../components/ToChkInstances';
import { useUser } from '../contexts/userContext';
import { useIns, useInsDispatch } from '../contexts/CartContext';
import { usefunctions } from '../contexts/functionContext';
import { formatNumber } from '../../utils/utils';
import { useRouter } from 'next/router';
import styles from '../../styles/cart.module.css';

export default function ToCheckOut() {
    const user = useUser();
    const router = useRouter();

    const { instances, itemCount, total, orders } = useIns();
    const {
        setInstance,
        setInsLen,
        setItemCount,
        setTotal,
        setOrders,
        setChkmsg,
    } = useInsDispatch();
    const { checkOut } = usefunctions();

    const handleChkOut = () => {
        checkOut(user, setInstance, setInsLen, setItemCount, setTotal);
        setChkmsg('checkout success!');
        //setTimeout(() => setChkmsg(''), 3000);
        router.push('/order');
    };

    const handleCancel = () => {
        router.push('/cart');
    };
    return (
        <Layout>
            {instances &&
                instances.filter((instance) => instance.check === true).length >
                    0 && (
                    <div className="container mt-3">
                        <div className="">You are going to check out: </div>
                        {instances
                            .filter((instance) => instance.check === true)
                            .map((checkedins) => {
                                return (
                                    <ToChkInstances
                                        checkedins={checkedins}
                                        key={checkedins._id}
                                    />
                                );
                            })}
                        <div className={styles.checkoutline}>
                            <span className="mr-3">
                                Total count: {itemCount}
                            </span>
                            <span className="mr-3">
                                Total price: {formatNumber(total)}
                            </span>
                            <button
                                className="btn btn-success mr-3"
                                onClick={handleChkOut}
                            >
                                confirm
                            </button>
                            <button
                                className="btn btn-warning"
                                onClick={handleCancel}
                            >
                                cancel
                            </button>
                        </div>
                    </div>
                )}

            {((instances &&
                instances.filter((instance) => instance.check === true)
                    .length === 0) ||
                !instances ||
                instances.length == 0) && <div>No product to check out</div>}
        </Layout>
    );
}
