export const FunctionContext = createContext();

const GetAllCarts = (parmsuser, fnins) => {
  console.log('parmsuser in functionConte: ', parmsuser);
  return fetch('api/user_getallcart_post', {
    method: 'post',
    body: JSON.stringify({
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      console.log('result.data in functionContext: ', result.data);
      fnins(result.data);
    })
    .catch(new Error());
};

const AddToCart = (parmscount, parmsid, parmsuser, fnins, fnlen) => {
  return fetch('/api/user_addtocart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      itemid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
