import styles from './about/about.module.css';
import { useRef, useState } from 'react';
export default function About() {
    return (
        <div>
            <div className="row">
                <button onMouseEnter={slideshowadd}>+</button>
                <div className="column">
                    <img
                        ref={img1ref}
                        src={images[count]}
                        alt="Nature"
                        onMouseEnter={click1}
                        height={100}
                        width={100}
                    />
                </div>
                <div className="column">
                    <img
                        ref={img2ref}
                        src={images[(count + 1) % images.length]}
                        alt="Snow"
                        onMouseEnter={click2}
                        height={100}
                        width={100}
                    />
                </div>
                <div className="column">
                    <img
                        ref={img3ref}
                        src={images[(count + 2) % images.length]}
                        alt="Mountains"
                        onMouseEnter={click3}
                        height={100}
                        width={100}
                    />
                </div>
                <div className="column">
                    <img
                        ref={img4ref}
                        src={images[(count + 3) % images.length]}
                        alt="Lights"
                        onMouseEnter={click4}
                        height={100}
                        width={100}
                    />
                </div>
                <button onMouseEnter={slideshowmin}>-</button>
            </div>

            <div className="container">
                <img id="expandedImg" src={image} height={400} width={400} />

                <div id="imgtext"></div>
            </div>
        </div>
    );
}
