import { useEffect, useRef, useState } from 'react';

import Link from 'next/link';
import Layout from '../../components/layout';
import { useRouter } from 'next/router';
import dbConnect from '../../utils/dbConnect';
import Item from '../../models/item';
import Image from 'next/image';
import styles from '../../styles/cart.module.css';
import { useInsDispatch } from '../../pages/contexts/CartContext';
import { useUser, useUserDispatch } from '../../pages/contexts/userContext';
import { usefunctions } from '../../pages/contexts/functionContext';
import { useInitial, useInitialDispatch } from '.././contexts/initialContext';
import { TransitionGroup } from 'react-transition-group';

export default function Post({ item }) {
  const nextrouter = useRouter();
  var temp;
  const { id } = nextrouter.query;
  const inputEL = useRef(null);
  const [incdis, setIncdis] = useState(false);
  const [decdis, setDecdis] = useState(false);

  const [stylestab1, setStylestab1] = useState(styles.tabactive);
  const [stylestab2, setStylestab2] = useState(styles.tab);
  const [stylestab3, setStylestab3] = useState(styles.tab);
  const [stylestab4, setStylestab4] = useState(styles.tab);

  const user = useUser();
  const setUser = useUserDispatch();

  const { GetAllCarts, AddToCart } = usefunctions();
  const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch();

  const setInitial = useInitialDispatch();
  const inital = useInitial();

  const handleCount = (e) => {
    var v = e.target.value.replace(/\D+/g, '').replace(/^0+/, '');
    inputEL.current.value = v;
    setIncdis(parseInt(inputEL.current.value) >= 999);
    setDecdis(parseInt(inputEL.current.value) <= 1);
  };

  const handleCart = () => {
    setIncdis(true);
    setDecdis(true);

    //hanle DB -> plus value
    AddToCart(
      inputEL.current.value,
      item._id,
      user,
      setInstance,
      setInsLen,
      setItemCount,
      setTotal
    ).then(() => {
      setIncdis(parseInt(inputEL.current.value) >= 999);
      setDecdis(parseInt(inputEL.current.value) <= 1);
    });
  };
  const handleIncrease = (e) => {
    inputEL.current.value = parseInt(inputEL.current.value) + 1;
    setIncdis(parseInt(inputEL.current.value) >= 999);
    setDecdis(parseInt(inputEL.current.value) <= 1);
  };

  const handleDecrease = (e) => {
    inputEL.current.value = parseInt(inputEL.current.value) - 1;
    setIncdis(parseInt(inputEL.current.value) >= 999);
    setDecdis(parseInt(inputEL.current.value) <= 1);
  };

  const handlefocus = () => {
    !inputEL.current.value && (inputEL.current.value = 1);
    setIncdis(parseInt(inputEL.current.value) >= 999);
    setDecdis(parseInt(inputEL.current.value) <= 1);
  };

  useEffect(() => {
    if (!inital) {
      //set user
      if (sessionStorage.getItem('key')) {
        temp = sessionStorage.getItem('key');
      } else {
        temp = Math.random()
          .toString(36)
          .replace(/[^a-z]+/g, '')
          .substr(0, 12);
        sessionStorage.setItem('key', temp);
      }
      setUser(temp);

      user &&
        GetAllCarts(user, setInstance, setInsLen, setItemCount, setTotal)
          .then(() => {
            setInitial(true);
          })
          .catch(new Error());
    }
    console.log('I am in effect2');
  }, [user]);

  useEffect(() => {
    setIncdis(parseInt(item.quantity) <= 1);
    setDecdis(parseInt(item.quantity) <= 1);
    console.log('I am in effect');
  }, [item._id]);

  // Render post...

  return (
    <Layout>
      <div key={item._id} className='container'>
        <div className='row mt-3'>
          <div className='col-6'>
            {/*
            <TransitionGroup
              transitionName='example'
              transitionEnterTimeout={500}
              transitionLeaveTimeout={300}
            >
              <Image
                src={`/${item.image}`}
                height={400}
                width={400}
                className={styles.img}
              />
            </TransitionGroup>*/}
            <Image
              src={`/${item.image}`}
              height={400}
              width={400}
              className={styles.img}
            />
          </div>

          <div className='col-6'>
            <h3 className='row-2'>{item.title}</h3>
            <p className='row-3'>Here is some introduce about this item...</p>
            <p className='row-4'>unit price: {formatNumber(item.price)}</p>
            <div className='row'>
              <div className='col-6'>
                <div className='d-flex flex-row flex-wrap'>
                  <div className={styles.inputwraper}>
                    <input
                      type='text'
                      ref={inputEL}
                      onInput={handleCount}
                      onBlur={handlefocus}
                      //  onChange={handleCount}
                      //    className='form-control'
                      defaultValue='1'
                      className={styles.input}
                    />

                    <button
                      className={styles.add}
                      onClick={() => handleIncrease(item)}
                      // className='border border-left-0 text-center'
                      // href=''
                      disabled={incdis}
                    >
                      +
                    </button>
                    <button
                      className={styles.minus}
                      // className='border border-left-0 border-top-0  text-center'
                      // href=''
                      onClick={() => handleDecrease(item)}
                      disabled={decdis}
                    >
                      -
                    </button>
                  </div>
                  <button className={styles.btnaddtocart} onClick={handleCart}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='row'>
          <ul className='col d-flex mt-3'>
            <button
              //   className='flex-fill'
              className={stylestab1}
              onClick={() => {
                setStylestab1(styles.tabactive);

                setStylestab2(styles.tab);

                setStylestab3(styles.tab);

                setStylestab4(styles.tab);
              }}
            >
              tab1
            </button>

            <button
              className={stylestab2}
              onClick={() => {
                setStylestab2(styles.tabactive);

                setStylestab1(styles.tab);

                setStylestab3(styles.tab);

                setStylestab4(styles.tab);
              }}
            >
              tab2
            </button>
            <button
              className={stylestab3}
              onClick={() => {
                setStylestab3(styles.tabactive);

                setStylestab1(styles.tab);

                setStylestab2(styles.tab);

                setStylestab4(styles.tab);
              }}
            >
              tab3
            </button>

            <button
              // className={styles.tab}
              className={stylestab4}
              onClick={() => {
                setStylestab4(styles.tabactive);

                setStylestab1(styles.tab);

                setStylestab2(styles.tab);

                setStylestab3(styles.tab);
              }}
            >
              tab4
            </button>
          </ul>
        </div>
        {stylestab1 === styles.tabactive && <p>I am tab1</p>}
        {stylestab2 === styles.tabactive && <p>I am tab2</p>}
        {stylestab3 === styles.tabactive && <p>I am tab3</p>}
        {stylestab4 === styles.tabactive && <p>I am tab4</p>}
      </div>
    </Layout>
  );
}

export async function getStaticPaths() {
  //get items from db like index
  // Call an external API endpoint to get posts
  await dbConnect();

  // find all the data in our database
  const result = await Item.find({});
  const items = JSON.parse(JSON.stringify(result));
  // Get the paths we want to pre-render based on posts
  const paths = items.map((item) => ({ params: { id: item._id } }));

  // We'll pre-render only these paths at build time.
  // { fallback: false } means other routes should 404.
  return {
    paths,
    fallback: false,
    //fallback: true or false // See the "fallback" section below
  };
}

// This also gets called at build time
export async function getStaticProps({ params }) {
  // params contains the post `id`.
  // If the route is like /posts/1, then params.id is 1

  await dbConnect();

  // find all the data in our database
  const result = await Item.findById(params.id);
  const item = JSON.parse(JSON.stringify(result));

  // Pass post data to the page via props
  return { props: { item } };
}
