import Head from 'next/head';
import Link from 'next/link';
import { useIns, useInsDispatch } from '../pages/contexts/CartContext';

import { useUser, useUserDispatch } from '../pages/contexts/userContext';

import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import NavDropdown from 'react-bootstrap/NavDropdown';
export default function Layout({
    children,
    title = 'This is the default title',
}) {
    const { inslen } = useIns();
    const { user, tmpuser } = useUser();
    const { setInstances } = useInsDispatch();
    const { setUser } = useUserDispatch();
    const handleLogout = () => {
        setUser('');
        setInstances('');
    };
    return (
        <div>
            <Head>
                <title>{title}</title>
                <meta charSet="utf-8" />
                <meta
                    name="viewport"
                    content="initial-scale=1.0, width=device-width"
                />
            </Head>
            <Navbar bg="light" expand="lg" sticky="top">
                <Navbar.Brand href="/">Home</Navbar.Brand>

                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <Link href="/about">
                            <a className="nav-link">About</a>
                        </Link>
                        <Link href="/contact">
                            <a className="nav-link">Contact</a>
                        </Link>
                    </Nav>
                    <Nav>
                        {/*! user*/}
                        <NavDropdown title="Hello" id="collasible-nav-dropdown">
                            <NavDropdown.Item>
                                <Link href="/order">
                                    <a>order</a>
                                </Link>
                            </NavDropdown.Item>
                            <NavDropdown.Item>
                                <Link href="/login">
                                    <a>Login</a>
                                </Link>
                            </NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.3">
                                Something
                            </NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="#action/3.4">
                                Separated link
                            </NavDropdown.Item>
                        </NavDropdown>
                        {user && (
                            <Link href="/">
                                <a className="nav-link"> Hello ({user})</a>
                            </Link>
                        )}
                        {!user && (
                            <Link href="/">
                                <a className="nav-link">Hello ({tmpuser})</a>
                            </Link>
                        )}
                        {console.log('user in layout: ', user)}
                        {!user && (
                            <Link href="/login">
                                <a className="nav-link">Login</a>
                            </Link>
                        )}
                        <a
                            className="nav-link"
                            style={{ cursor: 'pointer' }}
                            onClick={handleLogout}
                        >
                            Logout
                        </a>
                        <Link href="/cart">
                            <a className="nav-link">Cart ({inslen})</a>
                        </Link>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
            {children}
        </div>
    );
}
