import { body, validationResult } from 'express-validator';
import ItemInstance from '../../models/iteminstance';
import util from 'util';
const vb = () => {
  body('count')
    .trim()
    .isLength({ min: 1 })
    .escape()
    .custom((value) => {
      if (isNaN(parseInt(value))) {
        throw new Error('count shound be numebr');
      }
      return true;
    });
};
export default async function handler(req, res) {
  await vb();
  // Extract the validation errors from a request.
  const errors = validationResult(req);

  if (!errors.isEmpty()) {
    // There are errors. Render form again with sanitized values/errors messages.
    console.log(
      'errors+ ',
      util.inspect(errors, { showHidden: false, depth: null })
    );
    res.send('error for nan');
    return;
  }

  var count = parseInt(req.body.count);
  console.log('req.body: ', util.inspect(req.body, {}));
  console.log('count: ', count);
  var itemid = req.body.itemid;

  ItemInstance.findOneAndUpdate(
    { user: req.body.user, status: 'Cart', item: itemid },
    { $inc: { quantity: count }, buy_date: Date.now(), check: true }
  ).exec(function (err, items_cart) {
    console.log('err in itemInstance: ', err);
    if (err) {
      return new Error(err);
    } else {
      if (items_cart == null) {
        var iteminstance = new ItemInstance({
          item: itemid,
          user: req.body.user,
          quantity: count,
        });
        iteminstance.save(function (err) {
          if (err) {
            return new Error(err);
          }
          console.log('and new instance to itemInstance');
        });
      }
      // Successful - redirect to new record.
      res.send('success');
    }
  });
}
