import { useState } from 'react'
import Layout from '../../components/layout'
import Link from 'next/link'
import Image from 'next'
import ToChkInstances from '../../components/ToChkInstances'
import { useUserDispatch, useUser } from '../contexts/userContext'
import { useIns, useInsDispatch } from '../contexts/CartContext'
import { usefunctions } from '../contexts/functionContext'
import { formatNumber } from '../../utils/utils'

export default function ToCheckOut() {
    const user = useUser()
    const [orderon, setOrderOn] = useState(false)

    const { instances, itemCount, total, orders } = useIns()
    const {
        setInstance,
        setInsLen,
        setItemCount,
        setTotal,
        setOrders,
    } = useInsDispatch()
    const { checkOut, GetAllOrder } = usefunctions()

    const handleChkOut = () => {
        checkOut(user, setInstance, setInsLen, setItemCount, setTotal)
    }

    const handleOrders = () => {
        GetAllOrder(user).then((res) => {
            console.log('res in handleOrders: ', res)
            //(JSON.parse(JSON.stringify(res)))
        })
        setOrderOn(true)
    }

    return (
        <Layout>
            {instances &&
                instances.filter((instance) => instance.check === true).length >
                    0 && (
                    <div className="container">
                        <div className="">You are going to check out: </div>
                        {instances
                            .filter((instance) => instance.check === true)
                            .map((checkedins) => {
                                return (
                                    <ToChkInstances
                                        checkedins={checkedins}
                                        key={checkedins._id}
                                    />
                                )
                            })}
                        <div className="row">
                            <div className="mr-3">Total count: {itemCount}</div>
                            <div className="mr-3">
                                Total price: {formatNumber(total)}
                            </div>
                            <button
                                className="btn btn-success"
                                onClick={handleChkOut}
                            >
                                Confirm
                            </button>
                        </div>
                    </div>
                )}

            {((instances &&
                instances.filter((instance) => instance.check === true)
                    .length === 0) ||
                !instances ||
                instances.length == 0) && <div>No product to check out</div>}

            <button className="btn btn-success" onClick={handleOrders}>
                {' '}
                Your Orders
            </button>
            {orderon &&
                orders &&
                orders.map((order) => (
                    <div key={order._id} className="row">
                        <div className="col d-flex ">
                            <div>
                                <Link
                                    href="/store/[id]"
                                    as={`store/${order.item._id}`}
                                >
                                    <a className="btn btn-link btn-sm mr-2">
                                        <Image
                                            src={`/${order.item.image}`}
                                            height={70}
                                            width={70}
                                        />
                                    </a>
                                </Link>
                            </div>
                            <div className="align-self-center mr-2">
                                <Link
                                    href="/store/[id]"
                                    as={`store/${order.item._id}`}
                                >
                                    <a className="btn btn-link btn-sm  ">
                                        {order.item.title}
                                    </a>
                                </Link>
                            </div>

                            <div className=" align-self-center mr-2">
                                unit price: {formatNumber(order.item.price)}
                            </div>

                            <div className="align-self-center mr-3">
                                quantity: {order.quantity}
                            </div>
                        </div>
                    </div>
                ))}
        </Layout>
    )
}
