import React, { createContext, useContext, useReducer, useState } from 'react';
import CartReducer from './CartReducer';

export const CartContext = createContext();
export const CartContextAction = createContext();

const reducer = (state, action) => {
  switch (action.type) {
    case 'INCREASE':
      return state + 1;
    case 'DECREASE':
      return state - 1;
    case 'INCREASE_BY':
      return state + action.payload;
    default:
      throw new Error(`Unknown action: ${action.type}`);
  }
};

const CartContextProvider = ({ children }) => {
  //const [len, dispatch] = useReducer(reducer, 0);
  const [inslen, setInsLen] = useState(0);
  const [instances, setInstance] = useState('');

  const sumItems = (instances) => {
    //Storage(cartItems);
    let itemCount = instances.reduce(
      (total, instance) => total + instance.quantity,
      0
    );
    let total = instances
      .reduce(
        (total, instance) => total + instance.item.price * instance.quantity,
        0
      )
      .toFixed(2);
    return { itemCount, total };
  };

  const increase = (payload) => {
    dispatch({ type: 'INCREASE', payload });
  };

  const decrease = (payload) => {
    dispatch({ type: 'DECREASE', payload });
  };

  const addProduct = (payload) => {
    dispatch({ type: 'ADD_ITEM', payload });
  };

  const removeProduct = (payload) => {
    dispatch({ type: 'REMOVE_ITEM', payload });
  };

  const clearCart = () => {
    dispatch({ type: 'CLEAR' });
  };

  const handleCheckout = () => {
    console.log('CHECKOUT', state);
    dispatch({ type: 'CHECKOUT' });
  };

  const contextValues = {
    removeProduct,
    addProduct,
    increase,
    decrease,
    clearCart,
    handleCheckout,
  };

  return (
    <CartContext.Provider value={({ inslen, instances }, contextValues)}>
      <CartContextAction.Provider value={{ setInsLen, setInstance }}>
        {children}
      </CartContextAction.Provider>
    </CartContext.Provider>
  );
};

export const useIns = () => useContext(CartContext);
export const useInsDispatch = () => useContext(CartContextAction);
export default CartContextProvider;
