import { useEffect } from 'react';
import Link from 'next/link';
import Layout from '../components/layout';
import CartInstances from '../components/CartInstances';
import { useUserDispatch, useUser } from './contexts/userContext';
import { useIns, useInsDispatch } from './contexts/CartContext';
import { usefunctions } from './contexts/functionContext';
import { useInitial, useInitialDispatch } from './contexts/initialContext';
import { formatNumber } from '../utils/utils';
import styles from '../styles/cart.module.css';
import { useRouter } from 'next/router';

export default function Cart() {
    var temp;
    const router = useRouter();
    const user = useUser();
    const setUser = useUserDispatch();

    const { instances, itemCount, total } = useIns();
    const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch();
    const { GetAllCarts } = usefunctions();

    const setInitial = useInitialDispatch();
    const inital = useInitial();

    useEffect(() => {
        if (!inital) {
            console.log('I am in intial');
            //set user
            if (sessionStorage.getItem('key')) {
                temp = sessionStorage.getItem('key');
            } else {
                temp = Math.random()
                    .toString(36)
                    .replace(/[^a-z]+/g, '')
                    .substr(0, 12);
                sessionStorage.setItem('key', temp);
            }

            setUser(temp);

            user &&
                GetAllCarts(
                    user,
                    setInstance,
                    setInsLen,
                    setItemCount,
                    setTotal
                ).then(() => {
                    setInitial(true);
                });
        }
    }, [user, itemCount]);

    const handleCheckout = () => {
        if (
            instances &&
            instances.filter((instance) => instance.check === true).length > 0
        ) {
            router.push('/cart/checkout');
        } else {
            alert('no selected');
        }
    };
    return (
        <Layout>
            <div className="container mt-3">
                {console.log('instances : ', instances)}
                {instances &&
                    instances.map((iteminstance) => {
                        return (
                            <CartInstances
                                iteminstance={iteminstance}
                                key={iteminstance._id}
                            />
                        );
                    })}
                {(!instances || instances.length == 0) && <div>No items</div>}
                <div className={styles.checkoutline}>
                    <div></div>
                    <div>Total count: {itemCount}</div>
                    <div>Total price: {formatNumber(total)}</div>
                    <button
                        className={styles.checkoutbutton}
                        onClick={handleCheckout}
                    >
                        check out
                    </button>
                </div>
            </div>
        </Layout>
    );
}
