export default function Comment() {
    var temp;
    const user = useUser();
    const setUser = useUserDispatch();

    const { instances, itemCount, total, orders } = useIns();
    const {
        setInstance,
        setInsLen,
        setItemCount,
        setTotal,
        setOrders,
    } = useInsDispatch();
    const { GetAllCarts, checkdb, toCheckOut, GetAllOrder } = usefunctions();

    const setInitial = useInitialDispatch();
    const inital = useInitial();

    useEffect(() => {
        user && GetAllOrder(user, setOrders);
    }, [orders]);

    return (
        <Layout>
            <div className="container mt-3">
                <div className="row">
                    <table class="table">
                        <thead class="thead-light">
                            <tr>
                                <th>Your orders</th>
                                <th>name</th>
                                <th>unit price</th>
                                <th>quantity</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {orders &&
                                orders.map((order) => {
                                    return (
                                        <tr>
                                            <td>
                                                <Link
                                                    href="/store/[id]"
                                                    as={`store/${order.item._id}`}
                                                >
                                                    <a className="btn btn-link btn-sm mr-2">
                                                        <img
                                                            src={`/${order.item.image}`}
                                                            height={70}
                                                            width={70}
                                                        />
                                                    </a>
                                                </Link>
                                            </td>
                                            <td>
                                                <div className="align-self-center mr-2">
                                                    <Link
                                                        href="/store/[id]"
                                                        as={`store/${order.item._id}`}
                                                    >
                                                        <a className="btn btn-link btn-sm  ">
                                                            {order.item.title}
                                                        </a>
                                                    </Link>
                                                </div>
                                            </td>
                                            <td>
                                                <div className=" align-self-center mr-2">
                                                    {formatNumber(
                                                        order.item.price
                                                    )}
                                                </div>
                                            </td>
                                            <td>
                                                <div className="align-self-center mr-3">
                                                    {order.quantity}
                                                </div>
                                            </td>
                                            <td>
                                                <div className="align-self-center mr-3">
                                                    <Router>
                                                        <ReactLink
                                                            to={`/${order.item._id}/comment`}
                                                        >
                                                            Write Comment
                                                        </ReactLink>

                                                        <Switch>
                                                            <Route
                                                                path="/:id/comment"
                                                                children={
                                                                    <Comment />
                                                                }
                                                            />
                                                        </Switch>
                                                    </Router>
                                                </div>
                                            </td>
                                        </tr>
                                    );
                                })}
                        </tbody>
                    </table>
                </div>
            </div>
        </Layout>
    );
}
