import React, { createContext, useContext } from 'react';

export const FunctionContext = createContext();

const sumItems = (instances) => {
  let itemCount = instances.reduce(
    (total, instance) => total + instance.quantity,
    0
  );
  let total = instances
    .reduce(
      (total, instance) => total + instance.item.price * instance.quantity,
      0
    )
    .toFixed(2);
  return { itemCount, total };
};

const DeleteIns = (parmsid, parmsuser, fnins, fnlen, fnc, fnt) => {
  return fetch('/api/user_deleteins_post', {
    method: 'post',
    body: JSON.stringify({
      instanceid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      var { count, total } = sumItems(result.data);
      fnc(count);
      fnins(result.data);
      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);
      console.log('inslen in GetAllCartst: ', inslen);
    })
    .catch(new Error());
};

const ChangeAddCart = (parmscount, parmsid, parmsuser, fnins, fnlen) => {
  return fetch('/api/user_changeAddcart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      instanceid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      sumItems(result.data);
      fnins(result.data);

      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);
      console.log('inslen in GetAllCartst: ', inslen);
    })
    .catch(new Error());
};

const ChangeToCart = (parmscount, parmsid, parmsuser, fnins, fnlen) => {
  return fetch('/api/user_changetocart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      instanceid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      sumItems(result.data);
      fnins(result.data);
      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);
      console.log('inslen in GetAllCartst: ', inslen);
    })
    .catch(new Error());
};
const checkdb = () => {
  return fetch('/api/del_carts_all')
    .then(console.log('delete success in function'))
    .catch((err) => new Error(err));
};
const GetAllCarts = (parmsuser, fnins, fnlen) => {
  if (!parmsuser) return Promise.reject('no user');
  return fetch('/api/user_getallcart_post', {
    method: 'post',
    body: JSON.stringify({
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      sumItems(result.data);
      fnins(result.data);
      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;

      fnlen(inslen);
      console.log('inslen in GetAllCartst: ', inslen);
    })
    .catch(new Error());
};

const AddToCart = (parmscount, parmsid, parmsuser, fnins, fnlen) => {
  return fetch('/api/user_addtocart_post', {
    method: 'post',
    body: JSON.stringify({
      count: parmscount,
      itemid: parmsid,
      user: parmsuser,
    }),
  })
    .then((response) => response.json())
    .then((result) => {
      sumItems(result.data);
      fnins(result.data);
      const reducer = (accumulator, currentValue) =>
        accumulator + currentValue.quantity;
      var inslen = result.data ? result.data.reduce(reducer, 0) : 0;
      fnlen(inslen);
      console.log('inslen in GetAllCartst: ', inslen);
    })
    .catch(new Error());
};

const FunctionContextProvider = ({ children }) => {
  return (
    <FunctionContext.Provider
      value={{
        GetAllCarts,
        AddToCart,
        checkdb,
        ChangeToCart,
        ChangeAddCart,
        DeleteIns,
      }}
    >
      {children}
    </FunctionContext.Provider>
  );
};

export const usefunctions = () => useContext(FunctionContext);
export default FunctionContextProvider;
