import React, { createContext, useContext, useReducer } from 'react';
import { useUser, useUserDispatch } from '../contexts/userContext';

export const CartContext = createContext();
export const CartContext1 = createContext();

const dispatch = useUserDispatch();
const user = useUser();

const reducer = (state, action) => {
  switch (action.type) {
    case 'INCREASE': {
      if (false) {
        var user = userin;
      } else if (sessionStorage.getItem('key')) {
        user = sessionStorage.getItem('key');
      } else {
        user = Math.random()
          .toString(36)
          .replace(/[^a-z]+/g, '')
          .substr(0, 12);
        sessionStorage.setItem('key', user);
      }

      fetch('/api/user_addtocart_post', {
        method: 'post',
        body: JSON.stringify({
          count: 1,
          itemid: action.payload._id,
          user: user,
        }),
      })
        .then((response) => response.json())
        .then((data) => {
          console.log('data: ', data);
        });
      console.log('state: in CartContext', state);
      return state + 1;
    }

    case 'DECREASE':
      return state - 1;
    case 'INCREASE_BY':
      return JSON.parse(JSON.stringify(action.payload));
    default:
      throw new Error(`Unknown action: ${action.type}`);
  }
};
const CartContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, 0);

  return (
    <CartContext.Provider value={state}>
      <CartContext1.Provider value={dispatch}>{children}</CartContext1.Provider>
    </CartContext.Provider>
  );
};

export const useInstance = () => useContext(CartContext);
export const useInstanceDispatch = () => useContext(CartContext1);
export default CartContextProvider;
