import Link from 'next/link'
import Image from 'next/image'
import { useEffect, useState } from 'react'
import { useInsDispatch, useIns } from '../pages/contexts/CartContext'
import { useUser } from '../pages/contexts/userContext'
import { usefunctions } from '../pages/contexts/functionContext'
import { formatNumber } from '../utils/utils'

export default function ItemsInIndex({ item }) {
    //const { dispatch } = useInsDispatch();
    const { AddToCart } = usefunctions()
    const [busy, setBusy] = useState(false)

    const { setInstance, setInsLen, setItemCount, setTotal } = useInsDispatch()
    const { itemCount, total } = useIns()

    const user = useUser()
    const handleIncrease = (e) => {
        setBusy(true)
        AddToCart(
            1,
            item._id,
            user,
            setInstance,
            setInsLen,
            setItemCount,
            setTotal
        ).then(() => setBusy(false))
    }

    return (
        <>
            <div key={item._id}>
                <div className="card card-body">
                    <Link href="/store/[id]" as={`/store/${item._id}`}>
                        <a>
                            <Image
                                src={`/${item.image}`}
                                height={300}
                                width={300}
                                /*  style={{
              display: 'block',
              margin: '0 auto 10px',
              height: '200px',
              width: '200px',
            }}*/
                            />
                            <p className="item-name">{item.title}</p>
                        </a>
                    </Link>

                    <p className="text-left">
                        price: {formatNumber(item.price)}
                    </p>

                    <div className="text-right ">
                        <Link href="/store/[id]" as={`/store/${item._id}`}>
                            <a className="btn btn-link btn-sm ">detail</a>
                        </Link>

                        <button
                            onClick={() => handleIncrease(item)}
                            className="btn btn-outline-primary btn-sm"
                            disabled={busy}
                        >
                            Add more
                        </button>
                        {/*isInCart(product) && (
                <button
                  onClick={() => increase(product)}
                  className='btn btn-outline-primary btn-sm'
                >
                  Add more
                </button>
              )*/}

                        {/*!isInCart(product) && (
                <button
                  onClick={() => addProduct(product)}
                  className='btn btn-primary btn-sm'
                >
                  Add to cart
                </button>
              )*/}
                    </div>
                </div>
            </div>
        </>
    )
}
