import Layout from '../components/layout';
import ItemInstance from '../models/iteminstance';
import ItemsInIndex from '../components/itemsInIndex';
import { useItems } from './contexts/ProductsContext';
import { usedispatch } from './contexts/ProductsContext';
import { useEffect } from 'react';

export default function Index({ iteminstances }) {
  const state = useItems();
  const dispatch = usedispatch();

  const handleIncrease15 = (event) =>
    dispatch({
      type: 'INCREASE_BY',
      payload: iteminstances,
    });

  useEffect(() => {
    handleIncrease15();
  }, [iteminstances]);

  return (
    <Layout>
      {/* Create a card for each item */}
      <div className='card-columns'>
        {iteminstances.map((iteminstance) => (
          <ItemsInIndex iteminstance={iteminstance} key={iteminstance._id} />
        ))}
      </div>
    </Layout>
  );
}
/* Retrieves item(s) data from mongodb database  */
export async function getStaticProps() {
  if (sessionStorage.getItem('key')) {
    var user = sessionStorage.getItem('key');
  } else {
    user = Math.random()
      .toString(36)
      .replace(/[^a-z]+/g, '')
      .substr(0, 12);
    sessionStorage.setItem('key', user);
  }

  // find all the data in our database
  const result = await ItemInstance.find({ user: user });
  const iteminstances = JSON.parse(JSON.stringify(result));

  return { props: { iteminstances: iteminstances } };
}
