import Link from 'next/link';
import { useInslenDispatch } from '../pages/contexts/CartContext';

export default function CartInstances({ iteminstance }) {
  const dispatch = useInslenDispatch();
  const handleIncrease = (e) => {
    if (false) {
      var user = userin;
    } else if (sessionStorage.getItem('key')) {
      user = sessionStorage.getItem('key');
    } else {
      user = Math.random()
        .toString(36)
        .replace(/[^a-z]+/g, '')
        .substr(0, 12);
      sessionStorage.setItem('key', user);
    }

    dispatch({
      type: 'INCREASE',
      payload: iteminstance,
      user: user,
    });
  };
  const handleDecrease = (e) => {
    if (false) {
      var user = userin;
    } else if (sessionStorage.getItem('key')) {
      user = sessionStorage.getItem('key');
    } else {
      user = Math.random()
        .toString(36)
        .replace(/[^a-z]+/g, '')
        .substr(0, 12);
      sessionStorage.setItem('key', user);
    }

    dispatch({
      type: 'DECREASE',
      payload: iteminstance,
      user: user,
    });
  };

  return (
    <>
      <div key={iteminstance._id}>
        <div className='card card-body'>
          <img
            //src={iteminstance.item.image}
            style={{
              display: 'block',
              margin: '0 auto 10px',
              height: '200px',
              width: '200px',
            }}
          />
          <p className='item-name'>{iteminstance.item.title}</p>

          <h3 className='text-left'>price: {iteminstance.item.price}</h3>

          <div className='text-right'>
            <Link href='/[id]/detail' as={`/${iteminstance._id}/detail`}>
              <a className='btn btn-link btn-sm mr-2'>detail</a>
            </Link>

            <button
              onClick={() => handleIncrease(iteminstance)}
              className='btn btn-outline-primary btn-sm'
            >
              +
            </button>
            <button
              onClick={() => handleDecrease(iteminstance)}
              className='btn btn-outline-primary btn-sm'
            >
              -
            </button>
            {/*isInCart(product) && (
                <button
                  onClick={() => increase(product)}
                  className='btn btn-outline-primary btn-sm'
                >
                  Add more
                </button>
              )*/}

            {/*!isInCart(product) && (
                <button
                  onClick={() => addProduct(product)}
                  className='btn btn-primary btn-sm'
                >
                  Add to cart
                </button>
              )*/}
          </div>
        </div>
      </div>
    </>
  );
}
