import ItemInstance from '../../models/iteminstance';
import dbConnect from '../../utils/dbConnect';

import formidable from 'formidable';
import fs from 'fs';

import validator from 'validator';

export const config = {
    api: {
        externalResolver: true,
        bodyParser: false,
    },
};

export default async function handler(req, res) {
    const form = formidable({ multiples: true });
    // console.log('req.body: ', req.body);

    form.parse(req, (err, fields, files) => {
        console.log('I am in form parse');
        if (err) {
            console.log('err: ', err);
            res.json(err);
            return;
        }

        console.log('req.body.email: ', fields.email);
        console.log('req.body.password: ', fields.password);
    });

    var validatedtext, validatedinsid;
    var errors = [];
    await dbConnect();

    // validator text

    if (validator.isEmpty(validator.trim(fields.email))) {
        errors.push(' The insid is empty');
    } else {
        if (validator.isEmail(validator.trim(fields.email))) {
            validatedinsid = validator.escape(validator.trim(insid));
        } else {
            errors.push(' The insid is empty');
        }
    }

    if (errors.length > 0) {
        res.status(500).json({ error: errors, status: 500 });
        res.end();
        return;
    }

    ItemInstance.findByIdAndUpdate(validatedinsid, {
        commenttext: validatedtext,
        commentimage: filearray,
    })
        .populate('item')
        .then((items_comment) => {
            res.status(201).json({ status: 201, data: items_comment });
            res.end();
        })
        .catch((err) => {
            res.status(500).json({ status: 500, error: err });
            res.end();
        });
}
