import ItemInstance from '../../models/iteminstance';
import dbConnect from '../../utils/dbConnect';

export const config = {
  api: {
    externalResolver: true,
  },
};

export default async function handler(req, res) {
  req.body = JSON.parse(req.body);
  console.log('req.body: ', req.body);
  await dbConnect();

  ItemInstance.findOneAndUpdate(
    { user: req.body.user, status: 'Cart', item: req.body.itemid },
    { $inc: { quantity: req.body.count }, buy_date: Date.now(), check: true }
  )
    .then((res) => {
      if (res == null) {
        var iteminstance = new ItemInstance({
          item: req.body.itemid,
          user: req.body.user,
          quantity: req.body.count,
        });
        console.log('I am after new iteminstance');
        return iteminstance.save();
      }
    })
    .then(() => {
      console.log('I am beofre find');
      return ItemInstance.find({
        user: req.body.user,
        status: 'Cart',
      }).populate('item');
    })
    .then((items_carts) => {
      console.log('items_carts in user_addtocart_post: ', items_carts);
      res.status(201).json({ data: items_carts });
      res.end();
    })
    .catch((err) => {
      res.status(500).json({ error: err });
    });
