import Layout from '../components/layout';
import Item from '../models/item';
import ItemsInIndex from '../components/itemsInIndex';
import { useItems } from './contexts/ProductsContext';
import { usedispatch } from './contexts/ProductsContext';

export default function Index({ items }) {
  const state = useItems();
  const dispatch = usedispatch();

  const handleIncrease15 = (event) =>
    dispatch({
      type: 'INCREASE_BY',
      payload: items,
    });

  useEffect(() => {
    andleIncrease15;
  });
  return (
    <Layout>
      <button onClick={handleIncrease15} className='btn btn-primary'>
        {console.log('state: ', state)}
        click me
      </button>
      {/* Create a card for each item */}
